css:
@-moz-document domain("ccleaner.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #d20000;
        }

body, .band--shaded, .primary-nav>ul>li, .band, .page-foot, .primary-nav>ul>li>ul, .primary-nav>ul>li>ul>li>ul, .price-table td.col2, .price-table--ribbon thead td.product-column, thead td, thead th, .price-table thead, .price-table tbody td.feature-column, .pricing-table-footer__aside.band, .box, .pt--md.pb--md.mt--sm--neg, .page-body, .lang-select__dropdown__list, .scale-comparison-table--2col td:nth-child(1), .scale-comparison-table--2col th:nth-child(1), .scale-comparison-table--2col tr:nth-child(even) td:nth-child(1), .scale-comparison-table--2col tr:nth-child(even) th:nth-child(1), .scale-comparison-table--2col tr:nth-child(even) td:nth-child(3), .scale-comparison-table--2col tr:nth-child(even) th:nth-child(3), .homepage-body, .pricing-selector .pricing-selector__main-bodym, .block-buttons>.block-button, .block-buttons>.block-button:hover, .download-center-preview, .band--pulled-transparent__inner, .card-segment__card, .background-color--white, .business-pricing__input-area, .band--restricted .band__container-outer, .band--restricted--alt .band__container-inter, .partner-grid__item, .newsletter-signup-business .newsletter-signup-business__input-area, .newsletter-signup-business .newsletter-signup-business__input-area input {
    background: #111;
}
main.page-body:nth-of-type(2) {
    background: #111 !important;
}
.page-search__input, .text-input {
    background: #000;
    color: #eee;
}
.page-head {
    background: #111;
    border-bottom: 1px #222 solid;
}
.primary-nav>ul>li .dir:after {
    border-color: #555;
}
.band--separator, .page-foot, .page-foot__copyright, .primary-nav>ul>li>ul, .price-table td.col2, .box, .page-body, .lang-select__dropdown__list, .newsletter-signup .text-input, .block-buttons>.block-button, .block-buttons, .download-center-preview, .download-center-preview__row, .sub-nav li+li a, .sub-nav {
    border-color: #222;
}
.price-table--ribbon tbody td.col1, .price-table--ribbon tbody td.col2, .price-table--ribbon tbody td.feature-column, .price-table--ribbon tfoot td.col1, .price-table--ribbon tfoot td.col2, .price-table--ribbon tfoot td.feature-column, .br, .bl, .page-search__input, main.page-body:nth-of-type(2), .text-input {
    border-color: #222 !important;
}
.price-table--ribbon tbody td.feature-column, .price-table--ribbon tfoot td.feature-column {
    border-left: 5px solid #82a91c!important;
}
.price-table--ribbon thead td.product-column {
    border-color: #222 !important;
}
.site-logo, .page-search__submit {
    filter: invert(100)
}
.band--intro h1, .band--intro h2, .page-foot h5 a, .price-table--modern .product-column__title, .price-table .featuretitle, .color--darker, .pricing-selector .pricing-selector__product-title h4 span, .pricing-selector .pricing-selector__product-title h4 {
    color: #eee;
}
.text-mid, .page-foot a, .primary-nav__heading a, .primary-nav>ul>li .dir a, .text-icon-pair--horizontal .text-icon-pair--horizontal__title, .color--mid, .lang-select__list-header, .pricing-selector .pricing-selector__product-title p, p, li, .sub-nav a {
    color: #ccc;
}
html, .text-body {
    color: #977d60;
}
.scale-comparison-table--2col td:nth-child(1), .scale-comparison-table--2col th:nth-child(1) {
    border-right: 1px solid #222;
    border-left: 1px solid #222;
}
.scale-comparison-table--2col tr:last-child td {
    border-bottom: 1px solid #222;
}
.scale-comparison-table__inactive {
    color: #555;
}
.scale-comparison-table--2col td:nth-child(3), .scale-comparison-table--2col th:nth-child(3) {
    background: #111;
    border-left: 1px solid #222;
    border-right: 1px solid #222;
}
a.text-link--white.text-bold {
    background: #0000 !important;
}
.band--shaded-dark {
    background: #3f3e3e;
}
.box-ribbon---outer-red {
    background-color: #111;
    border-bottom: 4px solid #111;
}
.band--business-promo {
    background: #342c24 url(http://s1.pir.fm/pf/v6/business-bar.jpg) top center repeat-x;
}
.news-list__date, .pricing-selector .pricing-selector__addn-info, .pricing-selector .pricing-selector__quote {
    color: #aaa;
}
.page-foot__copyright, .page-foot__copyright a {
    color: #ddd;
}
.btn--white {
    background: #111;
    color: #eee;
}
.btn--white:hover, .btn--white:active, .btn--white:focus {
