    color: #eee;
}
hr, .product-grid .btn {
    background: #222;
}
.product-grid .btn.active, .product-grid .btn.focus, .product-grid .btn:active, .product-grid .btn:focus, product-grid .btn::hover {
    background: #111;
}
.download-center-preview__row h4 {
    color: #95bdd5;
}
