@-moz-document domain("codersblock.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #bbb;
        }

body, .box, .box-hello-social-icons a {
    background: #111;
}
.box-hello-avatar {
    border-color: #111;
}
h1, h2, h3, h4, h5, h6, h7, .post time, .box-coda-heading {
    color: #eee;
}
.post-html>*, p {
    color: #ccc;
}
.logo {
    color: #bbb;
}
.post-html a {
    border-color: #333;
}
:not(pre)>code, .gatsby-highlight pre {
    background: #222;
    border-color: #333;
}
.content-list>li:first-child, .content-list>li {
    border-color: #222;
}
.box-hello-social-icons a {
    border: 2px solid #97576d;
}
.projects-list-blurb, .box-hello-text {
    color: #bbb;
}
}

@-moz-document domain("cdpn.io") {
code {
    background: #111;
    border-color: #555;
}
body {
    background: #333;
    color: #cc6bb6;
}
label, body > p {
    background: #111;
}
}
