css:
[src*="/infernape.png"] {
    display: block;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    background: url(https://i.imgur.com/4R78BEF_d.webp?maxwidth=640&shape=thumb&fidelity=medium) no-repeat;
    background-size: contain !important;
    background-position: 50% 50% !important;
}

[src*="https://play.pokemonshowdown.com/sprites/gen5-back"] {
    transform: scaleX(-1)
}

[src*="https://play.pokemonshowdown.com/sprites/gen5-back"][src*="/toxtricity.png"] {
    transform: scaleX(1) !important
}

[src*="https://play.pokemonshowdown.com/sprites/gen5"][width="96"] {
    padding-right: 96px !important
}

[src*="https://play.pokemonshowdown.com/sprites/gen5"][style*="width: 96px"] {
    padding-left: 96px !important
}

[src*="https://play.pokemonshowdown.com/sprites/gen5"][style*="width: 144px"] {
    padding-left: 144px !important
}

[src*="https://play.pokemonshowdown.com/sprites/gen5"][style*="width: 192px"] {
    padding-left: 192px !important;
    background-size: contain !important
}

[src*="https://play.pokemonshowdown.com/sprites/gen5/corviknight"][style*="width: 192px"] {
    padding-left: 192px !important;
    background-size: 180% !important
}

[src*="https://play.pokemonshowdown.com/sprites/gen5"][style*="width: 288px"] {
    padding-left: 288px !important
}
