css:
@-moz-document domain("flightrising.com") {
.dragon-profile-header-back {
    top: 37px;
    display: none;
}

#dragon-profile-header {
    top: 30px;
    left: 12px;
}

#dragon-profile-header-controls {
    position: absolute;
    top: 14px;
    right: 6px;
    z-index: 10;
}



.dragon-profile-header-number {
    font-size: 16px;
    color: #888;
}

.dragon-profile-header-subtitle {
    font-size: 10px;
    text-transform: uppercase;
    font-style: italic;
    margin-left: 2px;
}

div.common-row {
    font-size: 12px;
}

#dragon-profile-likes {
    box-sizing: border-box;
    float: left;
    clear: left;
    margin-top: 1px;
    padding: 5px 10px 5px 5px;
    width: 81px;
    cursor: default;
}


div#dragon-profile-apparel {
    display: none;
}


div#dragon-profile-dragon {
    position: relative;
    left: 86px;
}

div#dragon-pdrofile-details {
    bottom: 20px;
}

#dragon-profile-familiar {
    left: 78px;
}


.dragon-profile-scene-scenic-dragon {
    position: relative;
    left: 100px;
}

.dragon-profile-scene-scenic-familiar {
    left: 442px;
}


div.common-column {
    text-align: left;
}

div.dragon-profile-lineage {
    height: 185px;
}
}
