css:
@-moz-document domain("login.taobao.com") {
#header .logo .link, .login-adlink, .view-type-qrcode, .footer {
    display:none;
}
body > #page {
    position:relative;
}
#header {
    position: absolute;
    z-index: 10;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -250px);
}
#header .logo {
    width:auto;
}
#content {
    height:100%;
}
.login-newbg {
    background-image: linear-gradient(-225deg, #2CD8D5 0%, #C5C1FF 56%, #FFBAC3 100%) !important;
    height:100% !important;
}
#content .content-layout {
    background: rgb(255, 255, 255, 0.5);
    box-shadow: 0 0 10px -1px #999;
    border-radius: 2em;
    width: 900px !important;
    height: 490px !important;
    position: absolute;
    top: 50%;
    transform: translate(-50%, -50%);
    left: 50%;
}
.login-box-warp {
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%);
}
}

@-moz-document domain("www.taobao.com") {
body > .layer, .tbh-nav, .screen-outer, body>.decorations-item, .tbh-fixedtool, .tbh-banner, #J_SiteFooter {
    display:none !important;
}
.site-nav-mobile, .site-nav-sitemap, .site-nav-service, .site-nav-pipe, .site-nav-seller, .site-nav-catalog, .tbh-qr-wrapper, .search-ft {
    display: none !important;
}
body {
    height:100vh;
    position: relative;
    background-size: cover;
    background-repeat: no-repeat;
    background-position: 50%;
    background-image: linear-gradient(-225deg, #2CD8D5 0%, #C5C1FF 56%, #FFBAC3 100%);
}
.J_Cover {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, 35px);
    z-index: 10;
}
.site-nav {
    border: none;
    background: transparent;
}
.site-nav .site-nav-bd {
    width: 630px;
    background: transparent;
}
.J_Cup {
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -160px);
    background: rgb(255, 255, 255, 0.7);
    height: auto;
    padding: 135px 0 20px 0;
    border-radius: 20px;
}
.J_Cup .top {
    padding:0;
    background: transparent;
}
.J_Cup .top .top-wrap {
    width:800px;
}
.tbh-search {
    width: 630px !important;
    margin: 0 !important;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
}
.search-bd .search-panel .search-button, .search-bd .search-panel .search-common-panel {
    border-radius: 0;
}
.search-bd .search-panel .search-common-panel:before {
    display:none;
}
.search-bd .search-panel .search-common-panel #q {
    background: rgb(255, 255, 255, 0.7);
}
.tbh-logo {
    position: absolute;
    top: -100px;
    left: 50%;
    transform: translateX(-50%);
}
.tbh-logo .logo {
    margin:0;
    height:auto;
}
}
