@-moz-document url("https://sync.deakin.edu.au/"), url("https://d2l.deakin.edu.au/d2l/home"), url-prefix("https://d2l.deakin.edu.au/") {
 * {
    border-color: #555555 !important;
}
html, html * {
    color: #eeeeee !important;
    background-color: #292929 !important;
}
.quick-view {
    background-color: #292929 !important;
}
.tile-container .tile.size-2x2 {
    color: #eeeeee !important;
    background-color: #292929 !important;
}
.item-link {
    color: #eeeeee !important;
}
.item-date {
    color: #eeeeee !important;
}
.tile-container {
    background-color: #292929 !important;
}
.tile {
    background-color: #292929 !important;
}
.low-priority {
    background-color: #292929 !important;
}
.content {
    background-color: #292929 !important;
}
.content.pads-small.loggable-section {
background-color: #292929;
}
.image {
    filter: grayscale(100%);
}
.tile-container .tile {
    background-color: #292929 !important;
}
.daylight .d2l-collapsepane .d2l-collapsepane-header {
	background: linear-gradient(to bottom, rgb(41 41 41 / 0%) 0, #292929);
    background-image: linear-gradient(rgba(41, 41, 41, 1) 0px, rgb(41, 41, 41));
}

.d2l-twopanelselector-side.d2l-twopanelselector-side-sep {
    background: linear-gradient(to right,rgba(249,250,251,0) 0,#f9fafb 320px);
    background-image: linear-gradient(to right, rgba(249, 250, 251, 0) 0px, rgb(41, 41, 41) 320px);
}
}
