}

.subtext.ytcp-video-metadata-advanced {
    color: var(--text-color) !important;
}

.subtext.ytcp-video-metadata-advanced > a {
    color: var(--theme-color) !important;
}

.subhead-compact.ytcp-video-metadata-advanced {
    color: var(--text-color) !important;
}

.label.ytcp-checkbox, ytcp-paper-checkbox.ytcp-checkbox > * {
    color: var(--text-color) !important;
}

#message-text.ytcp-comments-section {
    color: var(--text-color) !important;
}

.optin-header.ytgn-video-translations-section {
    color: var(--text-color) !important;
}

#row-container.ytgn-video-translation-row, #row-container.ytgn-video-translation-row .floating-column.ytgn-video-translation-row {
    background-color: var(--item-color);
}

#row-container.ytgn-video-translation-row.row-highlighted, #row-container.ytgn-video-translation-row.row-highlighted .floating-column.ytgn-video-translation-row {
    background-color: var(--theme-color);
}

.cell-body.ytgn-video-translation-row .language-text.ytgn-video-translation-row {
    color: var(--text-color) !important;
}

.language-text.ytgn-video-translation-row, .last-modified-text.ytgn-video-translation-row {
    color: var(--text-color) !important;
}

.translation-status.ytgn-video-translation-cell-captions {
