html{
--header-font: "Google Sans";
}

/* vidIQ dark */

.vidiq-toolbar-stats-container{
    background-color: var(--theme-add-color);
    border: none;
    border-radius: 5px;
    height: 36px;
    position: relative;
    top: 1.5px;
}

.vidiq-toolbar-stats-container *{
    position: relative;
    top: -0.2px;
}


.number, .unit{
    color: var(--text-color) !important;
}

.views-label{
    color: var(--text-darken-color) !important;
}

.cPxUoK{
    color: var(--text-color);
    background-color: var(--bg3-color);
    box-shadow: 0 0 5px var(--bg2-color);
    border-radius: 10px;
}

.livFkX:hover{
    background-color: #555;
    color: var(--text-color);
}

#stats-symbol{
    position: relative;
    top: 2px;
    color: #bbb !important;
}

.vidiq-dropdown-button-closed svg path{
    fill: var(--text-color) !important;
}

.vidiq-dropdown-button-open svg path{
    fill: var(--theme-color) !important;
}

.triangle{
    display: none;
}

.toolbar-stats-popover{
    border: none !important;
    border-radius: 10px !important;
    box-shadow: 0 0 5px var(--bg2-color);
}

.toolbar-stats-popover *{
    background-color: var(--bg3-color);
    border: none !important;
}

.popup-summary{
    background-color: var(--bg3-color) !important;
}

.avg-duration-value{
    color: var(--text-color) !important;
}

.stat-label{
    position: relative;
    top: -10px;
    background: none;
}

.footer .iq-row .upsell-section, .footer .iq-row .upsell-section p, .footer .iq-row .upsell-section p span, .footer .iq-row .upsell-section p span a{
    background-color: var(--item-color) !important;
}

.material-icons{
    color: var(--text-darken-color) !important;
}

.vidiq-key-search-term-container {
    margin-top: 20px;
}

.vidiq-subscriber-count {
    background-color: #0000;
    margin-left: 0px;
    font-size: 12px;
}

.vidiq-subscriber-count:before{
    content: "•";
    margin-right: 5px;
}

.vidiq-modal .modal-window{
    border-radius: 10px;
    background-color: var(--bg2-color);
}

.vidiq-modal .modal-header{
    border-radius: 10px 10px 0 0;
    padding: 15px 20px 15px 20px;
    background-color: var(--bg3-color);
    border: none;
}

.vidiq-modal .modal-close {
    right: 20px;
    top: 11px;
}

.vidiq-feedback-container {
    right: 55px;
    top: 14px;
}

.vidiq-modal .modal-title span {
    font-weight: 700;
    color: var(--text-color);
}

.vidiq-modal .modal-content {
    padding-bottom: 5px;
}

.vidiq-video {
    background-color: var(--bg3-color);
    padding: 0;
    border-radius: 10px;
    box-shadow: 0 0 5px 2px #0002;
    transition: .2s;
}

.vidiq-video:hover {
    background-color: var(--item-color);
    padding: 0;
    border-radius: 10px;
    transform: scale(1.05);
    transition: .2s;
    z-index: 10000000000;
}

.vidiq-video-thumbnail, .vidiq-video-thumbnail > img{
    height: 110px;
    width: 220px;
    object-fit: cover;
    border-radius: 10px 0 0 10px;
}

.vidiq-video-info {
    float: left;
    margin-top: 6px;
    margin-left: 10px;
    width: calc(100% - 240px);
}

.vidiq-video-meta {
    color: var(--text-darken2-color);
    font-size: 12px;
    vertical-align: middle;
}

.vidiq-video-channel-thumbnail {
    width: 26px;
    height: 26px;
    vertical-align: middle;
}

.vidiq-video-title {
    color: var(--text-color);
    font-family: var(--header-font);
    font-weight: 600;
    text-decoration: none !important;
}

.vidiq-modal .modal-sidebar-item-name {
    color: var(--text-color);
    font-family: var(--header-font);
    font-weight: 600;
}

.vidiq-react .button-group button {
    background-color: var(--bg3-color);
    border: none !important;
    transition: .1s;
}
.vidiq-react .button-group button:hover {
    background-color: var(--item-color);
    border: none !important;
    color: var(--text-darken-color);
    transition: .1s;
}

.vidiq-react .button-group .active, .vidiq-react .button-group .active:hover {
    color: var(--text-color); 
    opacity: 1;
    background-color: var(--item-color);
}

.vidiq-modal .modal-col-title.modal-col-title {
    background-color: var(--bg3-color);
    color: var(--text-color);
    border-radius: 5px;
}

.vidiq-modal .modal-sidebar-text-input {
    background-color: var(--bg3-color);
    border: none !important;
    padding: 6px;
    color: var(--text-color);
}

button.vidiq-button.secondary, .vidiq-button.secondary, button.vidiq-button.icon, .vidiq-button.icon, .vidiq-modal .vidiq-competition-view .playlist-top-keywords-button{
    border: none !important;
    text-shadow: none !important;
    box-shadow: none !important;
}

.vidiq-key-search-term-input {
    background-color: var(--bg3-color);
    border: none !important;
}

.vidiq-studio-beta-seo-score-container {
    border: none;
    border-radius: 10px;
}

header.vidiq-studio-beta-seo-score-tabs .stat-box-seo-score {
    border-radius: 10px 0 0 0;
    border: none;
}

header.vidiq-studio-beta-seo-score-tabs .stat-box-checklist {
    border-radius: 0 10px 0 0;
    border: none;
}

.stat-box-ranked-tags {
    border-radius: 0 0 0 10px;
    border: none;
}

.stat-box-high-volume-ranked-tags {
    border-radius: 0 0 10px 0;
    border: none;
}

header.vidiq-studio-beta-seo-score-tabs .stat-box{
   border: none;
}

.stat-box{
    background-color: var(--bg2-color);
    transition: .2s;
    border: none !important;
    box-shadow: none !important;
}

 .vc-stat-section .stat-box.alt{
    background-color: var(--bg3-color);
    transition: .2s;
    border: none !important;
    box-shadow: none !important;
}

.stat-box:hover {
    background-color: var(--bg3-color) !important;
    transition: .2s;
}

.stat-box.vidiq-selected {
    background-color: var(--bg3-color) !important;
    transition: .2s;
}

.vidiq-studio-beta-seo-score-left .seo-score-popover h1 {
    background-color: var(--item-color);
    color: var(--text-color);
    border: none;
}

.stat-value .out-of, .stat-value .currency, .stat-value .private-icon, .stat-value .approx {
    color: var(--text-darken-color);
}

.vidiq-studio-beta-seo-score-right li {
    color: var(--text-darken-color);   
}

.seo-checklist .checklist-arrow-link .material-icons {
    color: var(--text-color);   
    background-color: var(--bg2-color);   
}

.vidiq-studio-beta-seo-score-right{
    background-color: var(--bg3-color);
    border: none;
    border-radius: 0 0 10px 10px;
}

.vidiq-demonetization-words {
    border: none;
    background-color: var(--bg2-color);
    padding: 5px 0 5px 0;
    margin-top: 20px;
    border-radius: 10px;
}

.vidiq-studio-beta-tag-options {
    background-color: var(--bg2-color);
    border-radius: 5px;
    margin: 5px;
}

.vidiq-studio-beta-rec-tags-container {
    margin: 5px;
    margin-top: 20px;
    border: none;
    background-color: var(--bg2-color); 
}

.vidiq-studio-beta-rec-tags-header {
    color: var(--text-color);
    font-family: var(--header-font);
    font-size: 16px;
    border: none;
}

.vidiq-studio-beta-keyword, .vidiq-studio-beta-expandable-keywords-button {
    border: 0;
    background-color: var(--item-color);
    color: var(--text-color);
}

.vidiq-studio-beta-keyword-text {
    color: var(--text-color);
}

.vidiq-studio-beta-upsell {
    background-color: #46356b;
}

.vidiq-studio-beta-upsell-button {
    padding: 8px 15px;    
}

.vidiq-studio-beta-trending-vids-container {
    border: 0;
    background-color: var(--bg2-color);
}

.vidiq-studio-beta-trending-vids-search, .vidiq-studio-beta-trending-vids-search input{
    border: 0;
    background-color: var(--item-color);
    color: var(--text-color);
    padding-left: 0;
}

.vidiq-studio-beta-trending-vids-header, .vidiq-best-time-to-post-header{
    border: 0;
}

select.vidiq-studio-beta-select, .vidiq-studio-beta-trending-vids-sort.vidiq-studio-beta-select-container {
    background-color: var(--item-color);
    border: 0;
}

.vidiq-best-time-to-post-container {
    border: 0;
    background-color: var(--bg2-color);
}

.vidiq-studio-beta-settings-header:hover, .vidiq-studio-beta-settings-header.vidiq-expanded {
    background-color: var(--bg3-color);
}

.vidiq-studio-beta-settings-header img.vidiq-studio-beta-settings-arrow {
    margin-left: 10px;
}

.vidiq-upload-best-time {
    border: 0;
    background-color: #3378;
    border-radius: 5px;
    color: var(--text-color);
}

/* Wide Screen */

#left.ytcp-video-metadata-basics-old, .contents.ytcp-video-metadata-editor-old, ytcp-video-metadata-editor-old{
    max-width: none;
    --ytcp-primary-action-bar-capped-max-width: none;
}
