css:
@-moz-document domain("filecrypt.cc") {
html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 10px;
             background-color: #212121;
        }

::-webkit-scrollbar-thumb {
    border-radius: 3px !important;
    background: rgb(27, 145, 219) !important;
    -webkit-box-shadow: inset 0 0 6px rgba(0,0,0,.4) !important;
}

body, .create div.containersetup, div.midbox > div, .setup, .window, .content {
    background: #111;
    border-color: #111;
}
.content.contactform, .content.abuseform, .content.stats {
    background: #111 !important;
}
.topback path:nth-of-type(1) {
    fill: #111;
}
.container.window tr:nth-child(odd) td {
    background: #121212;
}
.container.window tr:nth-child(even) td {
    background: #111;
}
input[type='text'], input[type='number'], input[type='number'], input[type='email'], input[type='password'], div.createfolder textarea, select, input, textarea, .mirrorgroup.new button, .window.login input, input[type='text'], input[type='number'], input[type='number'], input[type='email'], input[type='password'], div.createfolder textarea, select, input, textarea, .mirrorgroup.new button, .window.login input, div.alert textarea, .content.abuseform textarea, .content.contactform textarea, .content.abuseform input, .content.contactform input, .content.midbox input {
    border: 1px solid #A3C0D1 !important;
    background: #b9e1ff5e !important;
    color: #eee;
}
.dfaa label {
    background: #b9e1ff5e !important;
    color: #00a1ff;
}
div.containersetup strong:after {
    background: #111;
}
ul.backup li, a, a:visited, a:hover, a:active, a:focus {
    color: #00a1ff;
}
}
