css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("keddr.com") {
/* Keddr.com Old Design v.0.1 by Dexogen*/
body{
	background: #f5f5f5;
    font-family: "PT Sans Caption", arial, sans-serif;
}
.review-box{
 	background: #777777;
}

.article-inner .title-wrap{
    background: #fff;
    color: #222;
}
header{
 	background: #262626;
    color: #fad400;
}
.login-btn, .categories a, .categories a:visited, .title-category, .Buttons, .btn, .decomments-button-send{
    background: #fad400 !important;
    color: #000 !important;
}

.login-btn:hover, .categories a:hover, .categories a:visited:hover, .title-category:hover, .Buttons:hover, .btn:hover, .decomments-button-send:hover {

    color: #FFF !important;
}

.search_input{
    background: #777777;
    color: #FFF !important;
}

.sitecontainer{
	padding-top: 10px;
}

.article-inner{
    border-bottom: 1px solid #dcdcdc;
}

.index-title:hover, .allrights a, .content a{
    color: #cb8d17 !important;
}

.widget widget_post_widget .author-link{
    color: #000 !important;
}

.magnifying-glass, .x{
	-webkit-filter:  brightness(100%) invert(50%);
	        filter:  brightness(100%) invert(50%);
}

.hamburger>div, .comments-count  svg  path{
    background: #fad400 !important;
}

.review-box, .sidebar{
  	margin-top: 10px;   
}
.review-post .author-link{
    color: #cbcbcb !important;
}

}
