css:
/*      MSN Login Page      */
/*        Grey/Green        */
/*       Version 1.2        */
/*         By f20k          */

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("login.live.com") {

/*--- Background Image ----*/
body{
color: #fff !important;
background: #3c3c3c url("http://i43.photobucket.com/albums/e374/frank11a/hm_bg.jpg") !important;
background-repeat: no-repeat !important; }

/*--- Sign up Button ----*/
.cssBtnDefault{background: #98d266 !important; border:1px solid #2b4702 !important;} 

/*--- Sign in Button ----*/
.cssBtnDefault input, table.cssBtn, .cssBtnHot input {background:#98d266 !important}

label{color: #fff !important;}              /*--- E-Mail Address, Save My Password Text Color ----*/

nobr{color: #000 !important}                /*--- Sign in button text, before clicking E-mail Address ----*/

#cttbl{padding-left: 450px  !important;}    /*--- Re-Position the center table ----*/

#footerTD{background: #333 !important}      /*--- Footer (Copyright, About, etc.) ----*/

#gradientDiv{display:none !important}       /*--- Top Image Gradient ----*/

#ValProp{color: #98d226 !important}         /*--- Info Message about Hotmail, Messenger, Xbox ----*/

.cssError{color: #98d226 !important}        /*--- Invalid Password/Email Address ----*/

.cssLT{background: none !important}         /*--- Background image of E-mail Address ----*/

a.cssUsername{color: #fff !important}       /*--- Email Address Text and Underline Colors ----*/

a.cssBlackLink, .cssSigning, a              /*--- Other Links colors ----*/
{color:#98d226 !important;} 

/*------ Replace 'Display Pic' Image ----*/
img[src*="http://img.wlxrs.com/~Live.SiteContent.ID/~14.2.1230/~/~/~/UT.png"],
img[src*="https://secure.wlxrs.com/~Live.SiteContent.ID/~14.2.1230/~/~/~/UT.png"] {
  width:          0px  !important;
  height:         1px  !important;
  padding-top:    24px !important;
  padding-left:   24px !important;
  padding-right:  25px !important;
  padding-bottom: 24px !important;
  margin-top:     2px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/msndp2.png) !important;
background-position: center !important;
}

/*--- Replace 'Windows Live' Image ----*/
img[src*="https://secure.wlxrs.com/~Live.SiteContent.ID/~14.2.1230/~/~/~/WindowsLive.png"],
img[src*="http://img2.wlxrs.com/~Live.SiteContent.ID/~14.2.1230/~/~/~/WindowsLive.png"]
{
  width:          0px  !important;
  height:         1px  !important;
  margin-top:     2px  !important;
  padding-top:    12px !important;
  padding-left:   87px !important;
  padding-right:  88px !important;
  padding-bottom: 12px !important;
background-image: url(http://i43.photobucket.com/albums/e374/frank11a/windowslive.png) !important;
background-position: center !important;
}

/*--- Login Page when accessed via Windows Live Messenger ----*/

.css9993{background:none !important} /*--- Left Iframe ----*/
.css9997{background:none !important} /*--- Right Iframe ----*/
.css9996{display:none !important}    /*--- Bottom Iframe ----*/

/*--- Sign in Box - Border ----*/
.css0086 { border-color: #050716 !important; border-width: 2px !important; margin-left: 100px !important;} 

/*--- Sign in Box - Top and Bottom Rows ----*/
.css0144, .css0147 {background: none!important; border-color: #050716 !important; border-width: 2px !important;}

/*--- Sign in Box - Middle Rows ----*/
.css0145, .css0146{background: #466D17 !important; border:0px !important}

/*--- End of Code ----*/
}
