@-moz-document domain("downloadgameps3.com"), domain("gamewii.net"), domain("gamepciso.com"), domain("downloadgamexbox.com"), domain("downloadgamepsp.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 5px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 5px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #33556e;;
        }

body, #outer-wrapper, .sidebar5 li:nth-child(2n+1), .sidebar5 li, #wpcomm .wc-field-textarea textarea, #wpcomm .wpdiscuz-front-actions, #wpcomm .wc-comment .wc-comment-left .wc-comment-label, div#sidebar-narrow, .widget-content, .post, .sidebar, #sidebar-wrapper, #sidebar1-wrapper {
    background: #111 !important;
}
#wpcomm .wc-comment .wc-comment-right {
    background: #131313;
}
.sidebar5 li:nth-child(2n+1) {
    border-top-color: #111;
}
.sidebar5 li:hover {
    background: #b3e225 url(https://2.bp.blogspot.com/-DZ7csl_JDJY/UtrGuHmQjxI/AAAAAAAAAIA/c004N-02S8U/s320/z2.gif) 3% 50% no-repeat !important;
}
.sidebar5 li a, .post li a {
    color: #518db9;
}
#sidebar-narrow, .post, #main-wrapper {
    border-color: #141414;
}
hr, #wpcomm .wpdiscuz-textarea-wrap, div.sharedaddy h3.sd-title:before, #wpcomm, #wpcomm .wc-form-footer, #wpcomm .wc-comment .wc-comment-left .wc-comment-label, #wpcomm .wc-comment-header, #wpcomm .wc-reply .wc-comment-header, .wp-pagenavi a, .wp-pagenavi span {
    border-color: #222;
}
#block-search-form .form-type-textfield input, #form_search_404 .form-text, #block-search-form .form-submit, #form_search_404 .form-submit {
    background: #111;
    border-color: #222;
    color: #eee;
}
b, h3, .post-body, #wpcomm .wc-field-textarea textarea, a:hover, #HTML2 {
    color: #eee;
}
.su-spoiler-style-fancy>.su-spoiler-title, .su-spoiler-style-fancy, .post blockquote, #wpcomm .wpd_label__check i.wpdicon-off, div#related-posts > a, .bar {
    background: #151515 !important;
    border-color: #222 !important;
    color: #eee;
}
#wpcomm .avatar {
    filter: invert(100);
    box-shadow: 0 0 3px 1px rgba(255, 255, 255, .1);
}
#wpcomm .wpd-form-row .wpdiscuz-item input[type=text], #wpcomm .wpd-form-row .wpdiscuz-item input[type=email], #wpcomm .wpd-form-row .wpdiscuz-item input[type=url], #wpcomm .wpd-form-row .wpdiscuz-item input[type=color], #wpcomm .wpd-form-row .wpdiscuz-item input[type=date], #wpcomm .wpd-form-row .wpdiscuz-item input[type=datetime], #wpcomm .wpd-form-row .wpdiscuz-item input[type=datetime-local], #wpcomm .wpd-form-row .wpdiscuz-item input[type=month], #wpcomm .wpd-form-row .wpdiscuz-item input[type=number], #wpcomm .wpd-form-row .wpdiscuz-item input[type=time] {
    background: #111;
    border-color: #222;
    color: #eee;
}
#wpcomm .wc-comment-label {
    -webkit-box-shadow: 0 4px 7px -5px #222;
    -moz-box-shadow: 0 4px 7px -5px #222;
    box-shadow: 0 4px 7px -5px #222;
}
div#related-posts > a > div {
    color: #eee !important;
    text-shadow: 0 2px 2px #333 !important;
}
.sidebar {
    text-shadow: 0 2px 2px #222;
}
div#HTML1.HTML.widget:nth-of-type(14) > .widget-content > div > span {
    color: #0080ff !important;
}
.sidebar, table {
    color: #bbb;
}
.bar h1 a, .bar h1 a:visited, .bar h2 a, .bar h2 a:visited {
    color: #eee;
    text-shadow: 0 1px 1px #222;
}
.status-msg-wrap {
    background: #252525;
    border-color: #333;
}
table th, table td, table th {
    border-color: #333;
}
}
