css:
@-moz-document domain("gamesas.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(255, 255, 255, .3);
             border-radius: 2px;
             background-color: #111;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 8px;
             background-color: #111;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(255, 255, 255, .3);
             background-color: #444;
        }

html, body {
    color: #fff;
    background-color: #5b5750;
}
.top-left, .top-center, .top-right, .bottom-right, .bottom-left, .bottom-center {
	display: none;
}
#left-border {
	background-image: none;
	padding: 10px;
}
#box {
	background: #000;
	border-radius: 23px;
}
.inside {
	background: #0000;
	color: #fff;
}
#right-border {
	background-image: none;
	padding: 10px;
}
.headerbar {
	border-radius: 6px;
}
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span {
	background-image: none;
}
.navbar {
	background: #222;
	border-radius: 6px;
}
ul.navlinks {
	border-color: #000;
}
a:visited, a:link, a.topictitle, a.topictitle:active, a:active {
	color: #fff;
}
.panel {
	background: #111;
	color: #cfcfcf;
	border-radius: 6px;
}
dl.faq dt {
	color: #c3c3c3;
}
h2 {
    color: #ddeaff;
}
.content h2, .panel h2 {
	color: #fff;
	border-color: #333;
}
.content {
	color: #ccc;
}
hr.dashed {
	border-color: #333;
}
hr {
	border-color: #000;
	border-top-color: #333;
}
a.top2 {
	filter: invert(100);
	color: #000;
}
label {
    color: #95a4bb;
}
select {
    border-color: #999;
    background-color: #0a0a0a;
    color: #fff;
}
a.button1, input.button1, input.button3, a.button2, input.button2 {
	background: #0a0a0a;
}
a.button2, input.button2, input.button3 {
	color: #999;
}
.headerbar {
	background-color: #000;
}
li.row:hover {
    background-color: #1a1805;
}
li.row {
	border-top-color: #000;
	border-bottom-color: #0b2a39;
}
ul.topiclist li {
	color: #fff;
}
ul.forums {
	background: linear-gradient(180deg, #0e0c0f, #000)
}
li.row:hover dd {
	border-color: #333;
}
ul.topiclist dd {
	border-color: #000;
}
dl.icon {
	filter: invert(100);
}
dl.icon dt, dd.posts, dd.topics, dd.views, dd.lastpost {
	filter: inherit;
}
.forumbg {
	border-radius: 6px;
}
.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {
    color: #bacbdb;
    background-color: #1C1D1e;
    border-color: #444a51;
}
.pagination span a:hover {
    border-color: #368AD2;
    background-color: #368AD2;
    color: #FFF;
}
.bg1 {
	background: #111;
}
.bg2 {
	background: #161616;
}
ul.topiclist dfn {
	display: none;
}
.postbody, dd label {
	color: #ccc;
}
.postprofile a:link, .postprofile a:active, .postprofile a:visited, .postprofile dt.author a, .postprofile strong, fieldset dl:hover dt label {
	color: #fff;
}
.postprofile {
	color: #999;
	border-color: #000;
}
fieldset.display-options label {
	color: #979797;
}
a.button1, input.button1, input.button3, a.button2, input.button2 {
	color: #fff;
}
.inputbox:hover {
	border-color: #1a1a1a;
}
.inputbox {
	background: #000;
	border-color: #444a51;
	color: #ccc;
}
.forabg {
	border-radius: 6px;
}
}
