css:
@-moz-document domain("gamesas.com") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 2px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 2px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #333;
        }

body {
    background: #111;
}
ul.navlinks, .postprofile, hr, ul.topiclist dd {
    border-color: #111;
}
#wrap, #left-border, .top-right, #right-border, span.corners-bottom span, span.corners-top span, .bottom-right, .bottom-left {
    background: #222;
}
 .navbar, .bg2, .bg1 {
    background: #333;
}
p.author, .postprofile, .pagination {
    color: #ccc;
}
.content, a:link, h3, a:visited, .postprofile a:link, .postprofile a:active, .postprofile a:visited, .postprofile dt.author a, .postprofile strong, ul.linklist li.rightside, p.rightside, ul.topiclist li, p, .inside {
    color: #eee;
}
select {
    background: #222;
    border-color: #333;
    color: #eee;
}
a.button1, input.button1, input.button3, a.button2, input.button2 {
    background: linear-gradient(180deg, #444, #333);
    border-color: #555;
    color: #eee;
}
ul.forums {
    background: #1e1e1e;
}
li.row:hover {
    background-color: #f6f4d059;
}
li.bg1.row > .icon, li.bg2.row > .icon {
    filter: invert(100);
    color: #000 !important;
}
a.topictitle, li.bg2.row > .icon > dt > [href], li.bg1.row > .icon > dt > [href], li.bg1.row > .icon > .lastpost > span > [href], li.bg2.row > .icon > .lastpost > span > [href] {
    color: #000;
}
ul.topiclist dd {
    border-color: #eee;
}
.pagination span a, .pagination span a:link, .pagination span a:visited, .pagination span a:active {
    background: #111;
    border-color: #222;
}
label {
    color: #708cb9;
}
}
