css:
@-moz-document url-prefix("http://www.baidu.com/"), url-prefix("https://www.baidu.com/"), url-prefix("http://baidu.com/"), url-prefix("https://baidu.com/"), url-prefix("https://ipv6.baidu.com/")
{
::selection {
    color: #fff !important;
    background: rgba(128, 210, 106, 0.4) !important;
}

body {
    background: url(https://i.loli.net/2020/06/13/oLxPXIeSZGyjEvM.png);
    background-attachment: fixed;
    background-size: cover;
}

body::-webkit-scrollbar {
    display: none;
}

/* 头部背景 */
.s-top-wrap {
    background: transparent;
}

/* 头部字体颜色 */
.c-color-t {
    color: #9195a3;
}

/*搜索框*/
#head_wrapper #kw {
    background: rgba(255, 255, 255, 0.1) !important;
    border: 2px solid rgba(128, 210, 106, 0.6);
    transition-duration: 0.5s;
    color: rgb(0, 255, 57);
    /* 字体颜色 */
}

/*搜索框：鼠标划过*/
#head_wrapper #kw:hover {
    background: rgba(255, 255, 255, 0.1) !important;
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
    border: 2px solid rgba(128, 210, 106, 1);
}

/*搜索框：鼠标点击*/
#head_wrapper #kw:focus {
    background: rgba(255, 255, 255, 0.3) !important;
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
    border: 2px solid rgba(128, 210, 106, 1);
}

/*搜索下拉框：上半部分*/
#kw.new-ipt-focus {
    border: 2px solid rgba(128, 210, 106, 1) !important;
}

/*取消 搜索下拉框 最上方的横线*/
.bdsug-new ul {
    padding-inline-start: 0px !important;
    border-top: none !important;
}

/* 字体颜色 */
.bdsug-overflow {
    color: rgb(0, 136, 2) !important;
}

/* 高亮字体颜色 */
.bdsug-s,
.bdsug-s b {
    color: rgb(0, 0, 255) !important;
}

/*搜索下拉框：下半部分*/
#head_wrapper #form .bdsug-new {
    margin-top: 7px;
    background: rgba(255, 255, 255, 0.3) !important;
    border-radius: 10px 10px 10px 10px;
    border: 2px solid rgba(128, 210, 106, 1) !important;
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
    font-family: Arial, "PingFang SC", "Microsoft YaHei", sans-serif;
    z-index: 1;
}

#head_wrapper #form .bdsug-new ul {
    margin: 7px 0px 0 0;
    padding: 8px 0 7px 14px;
    background: 0 0;
    border-top: 2px solid #f5f5f6;
}

#head_wrapper #form .bdsug-new ul li {
    padding-left: 14px;
}

/* 搜索下拉框：键盘移动 */
#head_wrapper #form .bdsug-new ul li.bdsug-s {
    width: 530px;
    background: rgba(100, 220, 250, 0.2) !important;
    box-shadow: 0px 0px 15px rgba(100, 220, 250, 1);
}

/*搜索下拉框：鼠标划过*/
#head_wrapper #form .bdsug-new ul li:hover {
    width: 530px;
    background: rgba(100, 220, 250, 0.2) !important;
    box-shadow: 0px 0px 15px rgba(100, 220, 250, 1);
}

/*“百度一下”按钮*/
#su {
    background: rgba(128, 210, 106, 0.5) !important;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
    line-height: 44px !important;
    border-left: none !important;
    transition-duration: 0.5s;
    position: absolute;
    color: rgb(255, 255, 255) !important
}

/*“百度一下”按钮：鼠标划过*/
#su:hover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
}

/*百度识图图标*/
.soutu-btn {
    background-color: transparent !important;
}

/* 底部背景 */
#bottom_layer {
    background: rgba(255, 255, 255, 0);
}
}
