    text-decoration: none;
    font-family: tahoma !important;
}

.gb_4 {
    color: #30402b !important;
    border: 1px solid #ca9d38 !important;
    font-weight: bold !important;
    outline: none !important;
    background: #f4c542 !important;
    background: -webkit-linear-gradient(top,#fd4c43,#eabe46) !important;
    background: linear-gradient(top,#4387fd,#4683ea) !important;
    /*filter: progid:DXImageTransform.Microsoft.gradient(startColorstr=#4387fd,endColorstr=#4683ea,GradientType=0) !important;*/
}

#fbar {
    background: #cecece !important;
    line-height: 40px !important;
    min-width: 980px !important;
    border-top: 1px solid #e4e4e4 !important;
}

.vk-t {
    height: 24px !important;
    cursor: move !important;
    padding: 0px 3px !important;
    color: white !important;
    font-style: italic !important;
    display: inline-block !important;
    overflow: hidden !important;
    width: 100% !important;
}

.vk-box {
    background-color: grey !important;
}
.b2hzT{
    display: none !important;
}

/* container properties */
.EHzcec {
    width:100% !important;
    max-width:100% !important;
    height:400px !important;
    background-color:#7bd6fc !important;
    overflow-y:scroll !important;
}

/* customize scrollbar css */
.EHzcec::-webkit-scrollbar{
    width:16px !important;
    background-color:#cccccc !important;
}
.EHzcec::-webkit-scrollbar:horizontal{
    height:16px !important;
}
.EHzcec::-webkit-scrollbar-track{
    border:1px #787878 solid !important;
    border-radius:0px !important;
    -webkit-box-shadow:0 0 6px #c8c8c8 inset !important;
}
.EHzcec::-webkit-scrollbar-thumb{
    background-color:#a73cb0 !important;
    border:1px solid #000000 !important;
    border-radius:0px !important;
}
.EHzcec::-webkit-scrollbar-thumb:hover{
    background-color:#bd46bf !important;
    border:1px solid #333333 !important;
}
.EHzcec::-webkit-scrollbar-thumb:active{
    background-color:#a639a2 !important;
    border:1px solid #333333 !important;
}
}

@document url("https://www.google.com/"), url("https://www.google.hu/"), url-prefix("https://www.google.com/webhp?hl=hu&sa=X"), url-prefix("https://www.google.hu/imghp?hl=hu&tab=wi&ogbl"), url-prefix("https://www.google.hu/webhp?tab=iw"), url-prefix("https://www.google.hu/webhp"), url-prefix("https://www.google.com/?") {
#gsr::before {
    position: absolute !important;
    top: 0 !important;
    left: 0 !important;
    width: 100% !important;
    height: 100% !important;
    content: " " !important;
    filter: hue-rotate(273deg) invert(100%) brightness(100%) !important;
    background-size: contain !important;
    background-image: url(https://web.archive.org/web/20200613111203if_/https://www.fire.gr/wp-content/uploads/2015/01/fireflame.jpg) !important;
}
}
