css:
@-moz-document url-prefix("https://elaws.e-gov.go.jp/search/elawsSearch/elaws_search/lsg0500/") {
/**
 *
 * 外側のレイアウト設定
 *
 **/
 #left_content, #right_content {
	height: 100vh !important;
}
.tab-content a {
	margin-left: 1em;
	color: inherit;
	text-decoration: none;
	line-height: 1.5em;
}
}

@-moz-document url-prefix("https://elaws.e-gov.go.jp/search/elawsSearch/elaws_search/lsg0500/") {
/**
 *
 * ブロック指定
 *
 **/

/** General **/
 .LawBody {
	line-height: 2em !important;
}
.Part {
	margin-top: 3em;
	margin-left: 1em;
}
.Chapter {
	margin-top: 2.5em;
	margin-left: 1em;
}
.Section {
	margin-top: 2em;
	margin-left: 1em;
}
.Article {
	margin-top: 1.5em;
	margin-left: 1em;
}
.ItemSentence, .ParagraphSentence {
	margin-top: 0.75em;
}
}

@-moz-document url-prefix("https://elaws.e-gov.go.jp/search/elawsSearch/elaws_search/lsg0500/") {
/**
 *
 * タイトル指定
 *
 **/
 .LawTitle {
	font-size: xx-large;
	margin: 2em 0;
}
.PartTitle {
	font-size: x-large;
	margin-left: 0;
}
.Chapter .ChapterTitle {
	font-size: large;
	margin-left: 0;
}
.Article .ArticleTitle, .Section .SectionTitle {
	font-size: larger;
	margin-left: 0;
}
.ArticleCaption {
	font-size: larger;
	margin-left: 0;
}
}
