css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.simplemachines.org") {
  
	/* ZOMG Choccy Curvaceous! */
	html {
		font-size: 62.5%;
	}
	/* Set a fontsize that will look the same in all browsers. */
	body {
		background: #15100C;
		font: 1.4rem/2rem "Segoe UI", Roboto, -apple-system, BlinkMacSystemFont, Arial, sans-serif;
		margin: 0 auto;
		padding: 3em 0 1em;
	}
	body, td, th, tr {
		color: #8f8a86;
	}
	/* Normal, standard links. */
	a:link, a:visited {
		color: #82705B;
	}
	/* Links that open in a new window. */
	a.new_win:link, a.new_win:visited {
		color: #82705B;
	}
	/* Sort out default 2.0.x text classes. */
	.smalltext, tr.smalltext th, .middletext, .normaltext {
		font-family: "Segoe UI", Roboto, -apple-system, BlinkMacSystemFont, Arial, sans-serif;
		font-size: 1.2rem;
		line-height: 1.8rem;
	}
	.middletext, .normaltext {
		font-size: 1.4rem;
		line-height: 2rem;
	}
  .largetext {
	  font-size: 2rem;
		line-height: 2.8rem;
  }
	/* We can style the different types of input buttons to be uniform throughout different browsers and their color themes.
		.button_submit - covers input[type=submit], input[type=button], button[type=submit] and button[type=button] in all browsers
		.button_reset  - covers input[type=reset] and button[type=reset] throughout all browsers
		.input_check   - covers input[type=checkbox] throughout all browsers
		.input_radio   - covers input[type=radio] throughout all browsers
		.input_text    - covers input[type=text] throughout all browsers
		.input_file    - covers input[type=file] throughout all browsers
	*/
	input, button, select, textarea {
		background: #1F1C19;
		color: #ccc;
		font: 1.4rem/2rem "Segoe UI", Roboto, -apple-system, BlinkMacSystemFont, Arial, sans-serif;
		border: 1px solid #483e34;
	}
	input, button, select {
		box-sizing: border-box;
		max-height: 2.4rem;
		min-height: 2.4rem;
		margin: 0;
		padding: 0 4px;
		border-radius: 2px;
		vertical-align: top;
	}
	select {
		padding: 0 2rem 0 4px;
		background-image: url('https://static.simplemachinesweb.com/smf/default/images/sort_down.gif');
		background-position: 98% 50%;
		background-repeat: no-repeat;
		-moz-appearance: none;
		-webkit-appearance: none;
		appearance: none;
	}
	/* For IE and Edge */ 
	select::-ms-expand { 
		display: none; 
	}
	select option	{
		padding: 1px 7px 1px 1px;
	}
	/* Buttons should be styled a bit differently, in order to make them look more button'ish. */
	.button_submit, .button_reset, #message_index_jump_to input, #display_jump_to input, #mlist_submit {
		padding: 0 4px;
		background: #201A15;
		font-weight: normal;
		border: 1px solid #6F5C45;
		border-radius: 2px;
		cursor: pointer;
	}
	#message_index_jump_to input, #display_jump_to input {
		font-weight: bold;
	}
	input:hover, textarea:hover, button:hover, select:hover	{
		border: solid 1px #8F7659;
	}
	.button_submit:hover, .button_reset:hover, #message_index_jump_to input:hover, #display_jump_to input:hover, #mlist_submit:hover {
		background: #32281F;
		color: #fff2cf;
		border: solid 1px #8F7659;
	}
	input:focus, textarea:focus, button:focus, select:focus	{
		border: solid 1px #8F7659;
	}
	/* Checkboxes or radio buttons shouldn't have a border around them. */
	h3.catbg input.input_check {
		margin: 9px 7px 0 7px;
	}
	/* Give disabled text input elements a different background color. */
	input[disabled].input_text {
		background: #eee;
	}
	/* Standard horizontal rule.. ([hr], etc.) */
	hr, .hrcolor {
		height: 1px;
		border: 0;
		background: #4D4030;
		color: #4D4030;
	}
	/* Standard headings. */
	h1, h2, h3, h4, h5, h6 {
		margin: 0;
		padding: 0;
		color: #6F5C45;
		font-size: 1em;
	}
	h3, h4 {
		padding-bottom: 0;
	}
	/* Fieldsets, to group elements. */
	fieldset {
		margin: 0 0 0.5em 0;
		padding: 1em;
		border: 1px solid #6F5C45;
		border-radius: 2px;
	}
	fieldset legend	{
		color: #7f7f7f;
		font-weight: bold;
	}
	/* Additional classes to fix Antiheinous's norty typos in ManageNews.template.php
	/* Antiheinous shouldn't do svn commits late at night.
	/* Mind you, nobody else spotted it either. :P */
	.floatleftpadding	{
		padding: 0.5em;
		float: left;
	}
	.floatrightpadding {
		padding: 0.5em;
		float: right;
	}
	/* End Antiheinous norty typos stuff. */
	/* Some BBC related styles.
	------------------------------------------------------- */
	/* A quote, perhaps from another post. */
	blockquote.bbc_standard_quote, blockquote.bbc_alternate_quote {
		overflow: auto;
		margin: 0 0 1rem 0;
		padding: 1em;
		font-size: 1.2rem;
		line-height: 1.6rem;
		border: solid #4D4030;
		border-width: 1px 0;
	}
	/* Alterate blockquotes. */
	blockquote.bbc_standard_quote	{
		color: #aaa;
		background-color: #201A15;
	}
	blockquote.bbc_alternate_quote {
		color: #999;
		background-color: #1A1511;
	}
	/* A code block - maybe PHP ;). */
	code.bbc_code	{
		padding: 3px 1em;
		background: #201A15;
		color: #7F7F7F;
		font-size: 1.1rem;
		line-height: 1.7rem;
		border: solid #584937;
		border-width: 1px 0;
	}
	/* The "Quote:" and "Code:" header parts... */
	.codeheader, .quoteheader	{
		color: #7F786F;
		font-size: 1.2rem;
		font-weight: 400;
	}
	/* Styling for BBC tags */
	.bbc_link:link, .bbc_link:visited, .bbc_link:hover, .bbc_link:focus	{
		text-decoration: underline;
		text-decoration-color: #463C31; 
		text-underline-position: under;
		border-bottom: 0;
	}
	.bbc_link:hover, .bbc_link:focus	{
		text-decoration-color: #85735D;
	}
	/* Generally, those [?] icons.  This makes your cursor a help icon. */
	/* /me uses this a lot. (emote, try typing /me in a post.) */
	.meaction	{
		color: red;
	}
	/* Highlighted text - such as search results. */
	.highlight {
		font-weight: bold;
		color: #C8712B !important;
		font-size: 1.4rem;
	}
	blockquote .highlight {
		font-size: 1.2rem;
	}
	code .highlight {
		font-size: 1.1rem;
	}
	/* A more discreet highlight color, for selected membergroups etc. */
	.highlight2, tr.highlight2 td	{
		background: #8F8674;
		color: #000 !important;
	}
	.highlight2 a	{
		color: #1f1400 !important;
		font-weight: bold;
	}
	/* Generic, mostly color-related, classes.
	------------------------------------------------------- */
	.titlebg, .titlebg2, tr.titlebg th, tr.titlebg td, tr.titlebg2 td	{
		color: #8F7659;
		font-family: "Segoe UI", Roboto, -apple-system, BlinkMacSystemFont, Arial, sans-serif;;
		font-size: 1.5rem;
		font-weight: 400;
		background: linear-gradient(#2D2821, #1D1710);
	}
	.catbg, .catbg2, tr.catbg td, tr.catbg2 td, tr.catbg th, tr.catbg2 th	{
		color: #FFDD99;
		font-family: "Segoe UI", Roboto, -apple-system, BlinkMacSystemFont, Arial, sans-serif;;
		font-size: 1.5rem;
		font-weight: 400;
		background: linear-gradient(#867664, #514130);
	}
	/* adjust the table versions of headers */
	tr.titlebg th, tr.titlebg2 th, td.titlebg, td.titlebg2,	tr.catbg th, tr.catbg2 th, td.catbg, td.catbg2 {
		padding: 0 6px;
	}
	tr.titlebg th a:link, tr.titlebg th a:visited, tr.titlebg2 td a:link, tr.titlebg2 td a:visited {
		color: #8F7659;
	}
	tr.catbg th a:link, tr.catbg th a:visited, tr.catbg2 td a:link, tr.catbg2 td a:visited {
		color: #FFDD99;
	}
	.catbg select {
		height: 2.4rem;
		font-size: 1.4rem;
	}
	/* Alternating backgrounds for posts, and several other sections of the forum. */
	.windowbg, .windowbg2, .windowbg3, #preview_body {
		color: #999;
		background-color: #14120E;
	}
	.windowbg2,	.windowbg3 {
		color: #8f8a86;
	}
	/* Colors for background of posts requiring approval */
	.approvebg,	.approvebg2, .approvetbg, .approvetbg2 {
		color: #BFB2A7;
		background-color: #352b21;
	}
	/* Sticky topics get a different background */
	.stickybg, .stickybg2, .stickybg2.lastpost + td {
		background-color: #201C16 !important;
	}
	/* Locked posts get a different shade, too! */
	.topic_table tr td.windowbg {
		background-color: #16130F;
	}
	.lockedbg, .lockedbg2	{
		background-color: #16130F;
		font-style: italic;
	}
	/* Posts and personal messages displayed throughout the forum. */
	.post, .personalmessage	{
		overflow: auto;
		line-height: 2rem;
		padding: 0.1em 0;
	}
	/* All the signatures used in the forum. */
	.signature, .attachments, .custom_fields_above_signature {
		width: 98%;
		overflow: auto;
		clear: right;
		padding: 1em 0 3px 0;
		font-size: 1.2rem;
		line-height: 1.6rem;
		border-top: solid 1px #4D4030;
	}
	/* Sometimes there will be an error when you post */
	.error {
		color: red;
	}
	/* Messages that somehow need to attract the attention. */
	.alert 	{
		color: red;
	}
	/* Calendar colors for birthdays, events and holidays */
	.birthday {
		color: #FFDD99;
	}
	.event {
		color: #DF9F53;
	}
	.holiday {
		color: #6F5C45;
	}
	/* Colors for warnings */
	.warn_mute {
		color: red;
	}
	.warn_moderate {
		color: #ffa500;
	}
	.warn_watch, .success {
		color: green;
	}
	a.moderation_link, a.moderation_link:visited {
		color: red;
		font-weight: bold;
	}
	.openid_login {
		background: #000 url(https://static.simplemachinesweb.com/smf/default/images/openid.gif) 2px 2px no-repeat;
		padding-left: 18px;
	}
	/* a descriptive style */
	.description, .description_board, .plainbox, .information {
		margin: 0.2em 1px 1em 1px;
		padding: 0.5em 1em;
		background: #0F0C0A;
		font-size: 1.2rem;
		line-height: 1.6em;
		border: 1px solid #3C3024;
		border-radius: 2px;
	}
	.description_board {
		margin: 1em 1px 0 1px;
	}
	/* an informative style */
	.information 	{
		background: #0A0A0A;
		border-color: #3F3F3F;
	}
	.information p {
		padding: 1em;
		margin: 0;
	}
	p.para2 {
		padding: 1em 0 3.5em 0;
		margin: 0;
	}
	/* AJAX notification bar
	------------------------------------------------------- */
	#ajax_in_progress {
		background: #000;
		border-bottom: 2px solid #FFDD99;
		color: #FFDD99;
		text-align: center;
		font-size: 16pt;
		padding: 0 8px;
		width: 100%;
		height: 1.7em;
		line-height: 1.6em;
		position: fixed;
		top: 0;
		left: 0;
	}
	#ajax_in_progress a {
		color: #FFDD99;
		text-decoration: underline;
		font-size: smaller;
		float: right;
		margin: 8px 20px;
	}
	/* Lists with settings use these a lot.
	------------------------------------------------------- */
	/* Styles for rounded headers.
	------------------------------------------------------- */
	h3.catbg, h3.catbg2, h3.titlebg, h4.titlebg, h4.catbg {
		height: auto;
		font-size: 1.6rem;
		line-height: 3.4rem;
		font-weight: normal;
	}
	h3.catbg a:link, h3.catbg a:visited, h4.catbg a:link, h4.catbg a:visited,
	h3.catbg, .table_list tbody.header td, .table_list tbody.header td a {
		color: #FFDD99;
	}
	h3.catbg2 a, h3.catbg2 {
		color: #FFDD99;
	}
	h3.catbg a:hover, h4.catbg a:hover, .table_list tbody.header td a:hover {
		color: #FFEFCF;
		text-decoration: none;
	}
	h3.catbg2 a:hover {
		color: #FFEFCF;
		text-decoration: none;
	}
	h3.titlebg a, h3.titlebg, h4.titlebg, h4.titlebg a {
		color: #8F7659;
	}
	h3.titlebg a:hover, h4.titlebg a:hover {
		color: #BF935F;
		text-decoration: none;
	}
	h3.catbg img.icon, h4.titlebg img.icon {
		vertical-align: middle;
		margin: -2px 5px 0 0;
	}
	h4.catbg a.toggle img {
		vertical-align: middle;
		margin: -2px 5px 0 5px;
	}
	h4.catbg, h4.catbg2 , h3.catbg , h3.catbg2 , .table_list tbody.header td.catbg {
		background: linear-gradient(#867664, #514130);
		padding: 0;
	}
	h4.titlebg, h3.titlebg {
		background: linear-gradient(#2D2821, #1D1710);
		padding: 0;
	}
	h4.titlebg img.icon {
		float: left;
		margin: 5px 8px 0 0;
	}
	div.cat_bar, div.title_bar {
		height: auto;
		margin-bottom: 1px;
		padding: 0 9px;
		background: linear-gradient(#867664, #514130);
		border-radius: 5px;
	}
	div.title_bar	{
		background: linear-gradient(#2D2821, #1D1710);
	}
	/* rounded bars needs a different background here */
	div.roundframe div.cat_bar {
		background: linear-gradient(#867664, #514130);
		margin-bottom: 0;
	}
	div.roundframe div.cat_bar h3.catbg {
		background: none;
	}
	div.title_barIC {
		height: auto;
		margin: 4px 0;
		padding: 0 9px;
		background: linear-gradient(#2D2821, #1D1710);
		border-radius: 5px;
	}
	div.title_barIC h4.titlebg {
		background: none;
	}
	#upshrinkHeaderIC p.pminfo {
		margin: 0;
		padding: 0.5em;
	}
	img#upshrink_ic, img#newsupshrink	{
		float: right;
		margin: 10px 5px 0 0;
	}
	/* The half-round header bars for some tables. */
	.table_grid tr.catbg, .table_grid tr.titlebg {
		background: none;
		font-size: 1.3rem;
		border-bottom: 1px solid #000;
	}
	.table_grid tr.catbg th, .table_grid tr.titlebg th {
		height: 3.2rem;
		background: linear-gradient(#867664, #514130);
		line-height: 3.2rem;
	}
	.table_grid tr.titlebg th {
		background: linear-gradient(#2D2821, #1D1710);
	}
	tr.catbg th.first_th {
		border-radius: 5px 0 0 0;
	}
	tr.catbg th.last_th	{
		border-right: 0;
		border-radius: 0 5px 0 0;
	}
	tr.titlebg th.first_th {
		border-radius: 5px 0 0 0;
	}
	tr.titlebg th.last_th {
		border-radius: 0 5px 0 0;
	}
	.table_grid th.last_th input {
		margin: 0 2px;
	}
	.table_grid th.lefttext	{
		padding: 0 0.7em;
	}
	/* a general table class */
	table.table_grid {
		border-collapse: collapse;
		margin-top: 1px;
	}
	table.table_grid td	{
		padding: 3px;
		border-bottom: 1px solid #000;
		border-right: 1px solid #000;
	}
	/* GenericList */
	.additional_row {
		padding: 0.5em 0 0.5em 0;
	}
	table.table_grid thead tr.catbg th {
		white-space: nowrap;
	}
	/* Common styles used to add corners to divisions.
	------------------------------------------------------- */
	.topslice, .botslice {
		visibility: hidden !important;
	}
	/* Used for sections that need somewhat larger corners.
	----------------------------------------------------------- */
	.roundframe	{
		padding: 10px;
		background: #0F0D0A;
		border: 1px solid #483e34;
		border-radius: 8px;
	}	
	span.upperframe, span.lowerframe {
		height: 0;
		background: none;
	}
	span.upperframe span, span.lowerframe span {
		display: none;
	}
	.roundframe dl, .roundframe dt, .roundframe p	{
		margin: 0;
	}
	.roundframe p	{
		padding: 0.5em;
	}
	/* Styles used by the auto suggest control.
	------------------------------------------------------- */
	.auto_suggest_div {
		border: 1px solid #6F5C45;
		position: absolute;
		visibility: hidden;
	}
	.auto_suggest_item {
		background: #000;
	}
	.auto_suggest_item_hover {
		background: #1F1F1F;
		cursor: pointer;
		color: #8F8F8F;
	}
	/* Styles for the standard dropdown menus.
	------------------------------------------------------- */
	#main_menu {
		float: none;
		width: auto;
		margin: 0;
		padding: 0;
	}
	.dropmenu, .dropmenu ul {
		list-style: none;
		margin: 0;
		padding: 0;
	}
	.dropmenu {
		padding: 0 5px;
	}
	.dropmenu>li {
		display: inline-block;
		position: relative;
		margin: 0 2px;
		padding: 0;
	}
	.dropmenu a {
		display: block;
		color: #82705B;
		text-decoration: none;
	}
	.dropmenu a span 	{
		display: block;
		padding: 0 5px;
		font-size: 1.2rem;
	}
	/* Backgrounds: first level only. */
	.dropmenu li a.firstlevel, #site_nav li a.firstlevel {
		margin: 0;
		border-radius: 3px;
	}
	.dropmenu li a.firstlevel span.firstlevel {
		position: static;
		height: auto;
		line-height: 2.2rem;
	}
	/* Active button. */
	.dropmenu li a.active, #site_nav li a.active {
		background: #D68543;
		color: #fff;
		text-shadow: 0 0 1px #000;
	}
	/* Hover effects: first level only. */
	.dropmenu li a.firstlevel:hover, .dropmenu li:hover a.firstlevel {
		background: #71614F;
		color: #fff;
		cursor: pointer;
		text-decoration: none;
		transition: background .07s step-end, color .07s step-end;
	}
	.dropmenu li a.active:hover	{
		background: #71614F;
		color: #ffefcf;
	}
	.dropmenu li a.active span.firstlevel, #site_nav li a.active span.firstlevel,
	.dropmenu li a.firstlevel:hover span.firstlevel, .dropmenu li:hover a.firstlevel span.firstlevel,
	.dropmenu li a.active:hover span.firstlevel	{
		background: none;
	}
	.dropmenu li ul {
		display: block;
		position: absolute;
		z-index: 9;
		top: 2.7rem;
		left: -3000px;
		width: 16em;
		padding: 6px 0;
		font-weight: normal;
		background: #000;
		border: solid 1px #4F4131;
		border-radius: 4px;
		box-shadow: 2px 3px 5px 1px #000;
		opacity: 0;
		transition: left .3s step-end, opacity .3s linear;
	}
	.dropmenu ul::before {
		display: block;
		position: absolute;
		z-index: -1;
		top: -6px;
		right: -6px;
		left: -6px;
		bottom: -2em;
		content: '';
	}
	.dropmenu li li {
		position: relative;
		width: 16em;
		margin: 0;
		border: 0;
	}
	#admin_menu .dropmenu li ul, #admin_menu .dropmenu li li {
		width: 19em;
	}
	.dropmenu li li a span {
		padding: 8px;
	}
	.dropmenu li ul ul {
		margin: -2.4em 0 0 15em;
	}
	/* Hover effects: levels 2 and 3. */
	.dropmenu li li a.chosen	{
		color: #AA8E6F;
		font-weight: bold;
	}
	.dropmenu li li a:hover, .dropmenu li li:hover>a {
		background: #7F694F;
		color: #fff;
		text-decoration: none;
	}
	.dropmenu li li:hover ul, .dropmenu li:hover ul {
		top: 2.7rem;
		left: -4px;
		opacity: 1;
		transition: left .1s step-end, opacity .3s linear;
	}
	.dropmenu li li.additional_items {
		background: #000;
	}
	/* Secondary menus. */
	#adm_submenus {
		padding-left: 2em;
		overflow: hidden;
	}
	/* Styles for sidebar menus.
	------------------------------------------------------- */
	.adm_section .cat_bar {
		border-radius: 3px;
	}
	.adm_section h4.catbg {
		font-size: 1.4rem;
    line-height: 2.8rem;
	}
	.left_admmenu {
    padding: 6px 0 8px;
  }
	.left_admmenu li {
		font-size: 1.3rem;
    line-height: 2.3rem;
  }
	/* The dropdown menu toggle. */
	#menu_toggle {
		float: right;
		margin-right: 10px;
		padding-top: 3px;
	}
	#menu_toggle span	{
		position: relative;
		right: 5000px;
	}
	/* Styles for the standard button lists.
	------------------------------------------------------- */
	.buttonlist ul li	{
		margin: 0 2px;
	}
	.buttonlist ul li a	{
		margin: 0;
		padding: 0;
		background: #2c2c2c;
		color: #8f7659;
		font-size: 1.1rem;
		border-radius: 3px;
	}
	.buttonlist ul li a span {
		background: none;
		height: auto;
		line-height: 2rem;
		padding: 0 8px;
	}
	.buttonlist ul li a:hover {
		background: #71614F;
		text-decoration: none;
	}
