	#footer_section::before, #footer_section::after {
		position: absolute;
		top: 0;
		display: block;
		content: '';
	}
	#footer_section::before {
		right: 0;
		left: 0;
		height: 44px;
		background: linear-gradient(#000, #20190E);
		border: solid #393939;
		border-width: 0 1px 1px;
		border-radius: 0 0 9px 9px;
	}
	#footer_section::after {
		right: 8px;
		left: 8px;
		height: 32px;
		background: #000;
		border-radius: 0 0 7px 7px;
	}
