  /* Go up/go down links. Not available on all pages. */
  a[href="#top"], a[href="#bot"], a[href="#lastPost"] {
    position: fixed;
    top: 50%;
    left: -4px;
    width: 1.25em;
    height: 20vh;
    overflow: hidden;
    padding-bottom: 10vh;
    background: #423226;
    color: #777;
    font-size: 2em;
    font-weight: 700;
    line-height: 20vh;
    text-align: center;
    white-space: nowrap;
    border: 1px solid #2C2119;
    border-radius: 0 0 .5em 0;
    opacity: .7;
  }
  a[href="#top"] {
    top: auto;
    bottom: 50%;
    padding-top: 10vh;
    padding-bottom: 0;
    border-radius: 0 .5em 0 0;
  }
  a[href="#top"]:hover, a[href="#bot"]:hover, a[href="#lastPost"]:hover {
		background: #584232;
    color: #999;
    opacity: 1;
    box-shadow: 1px 2px 1px #0009;
  }
  a[href="#top"]::before, a[href="#bot"]::before, a[href="#lastPost"]::before {
    display: inline-block;
    width: 1.25em;
    content: '\2193';
