css:
@-moz-document url("http://hvh.aimwa.re/"), url-prefix("https://aimware.net") {
body, .header-search-container {
	background-color: #1a1a1a;
    color: #ffffff;
    font-family: titillium web,sans-serif;
}

.section-base, .board-notice {
	background: #2a2a2a;
    box-shadow: 0px 0px;
}

.section-forum-top {
	background: #222222;
    border-left: 3px solid #ff4544
}

.breadcrumb {
	background: #2a2a2a;
}

.section-breadcrumbs {
    box-shadow: 0px 0px;
}

.forum-poster-information {
	background: #4a4a4a;
}

.section-title-child, .form-control.text:focus, div.sceditor-group {
	background: #ffffff;
    color: #ffffff;
}

.page-link:hover, .page-link {
	background: #4a4a4a;
    color: #ffffff;
    border: 0px;
}

.sceditor-container {
	background: #2a2a2a;
    color: #ffffff;
}

div.sceditor-toolbar {
	background: padding-box #2a2a2a;
    color: #ffffff;
}

.breadcrumb-item.active, .table, .sceditor-button div, h1, h2, h3, h4, .section-sub-title a {
    color: #ffffff;
}

.forum-thread-announcement {
	background: #1a1a1a;
}

.forum-thread-important, .list-group-item {
	background: #1E1E1E;
}

header.header, .form-control {
    color: #FFFFFF !important;
    background: #2a2a2a !important;
    box-shadow: 0 0 0 !important;
}

.form-control:focus {
	border-color: #e74c3c !important
}

*, ::before, ::after {
	scrollbar-width: none;
}

input[data-shoutbox-send-message=""] {
	border-color: #595757 !important
}

.navbar-brand {
	height: 0 !important;
	width: 0 !important;
	/* these numbers match the new image's dimensions */
	padding-left: 386px !important;
	padding-top: 88px !important;
    padding-bottom: 0px !important;
	background: url(https://i.ibb.co/3CSm0QT/main-standards.png) no-repeat !important;
}

div[id="chat-list"] {
	border: 1px solid #595757 !important;
	border-radius: 0.25rem;
	padding: 10px 10px 10px 10px;
}

div[id="chat-list"] > span[style="color: rgba(165,165,165,0.67)"]::before {
	content: "["
}

div[id="chat-list"] > span[style="color: rgba(165,165,165,0.67)"]::after {
	content: "]"
}

@keyframes rainbow { 
    0%{background-position:0% 0%}
    50%{background-position:100% 0%}
    100%{background-position:0% 0%}
}

a[href="/forum/user/59758"] > strong > span {
    background: linear-gradient(to right, #6666ff, #0099ff , #00ff00, #ff3399, #6666ff);
    background-clip: text;
    color: transparent !important;
    animation: rainbow 6s ease-in-out infinite;
    -webkit-background-clip: text;
    background-size: 1800% 1800%;
}

@keyframes fade {
    0%,100% {
        opacity: 90%;
    }

    50% {
        opacity: 100%;
    }
}

span[style="color:#309830"] > strong {
    animation: fade 0.8s ease-in-out infinite;
    color: #00b42b !important
}

.section-title-child {
	background-color: #3a3a3a
}

div[class="container"] > a[class="button-base button-user-top"] {
	background: #2a2a2a;
	border-left: 3px solid #ff4544;
    padding: 5px 20px 5px 20px;
    margin-right: 10px;
}

div[class="container"] > a[class="button-base button-user-top"] > span {
    margin-top: 30px;
}

section[class="section-breadcrumbs"] {
	margin-top: 35px;
}

section[class="col-lg-12 board-notice section-content"] {
	margin-top: 20px
}

.header-search-container {
    padding-top: 0px;
    min-height: 45px;
}

.thread-newpost {
	color: #cecece
}

.thread-nonewpost {
	color: #5a5a5a
}
}

