css:
@-moz-document url-prefix('https://docs.google.com/')
  {
.left-sidebar-container {
    width: 470px !important;
}
.navigation-widget {
    width: 470px !important;
    background: #1afbfc;
}
.kix-page-compact:before {
    border: none;
}
.docs-gm #docs-editor, .docs-gm #docs-editor-container {
    background:#12202f;
}
#docs-toolbar-wrapper {
    border-top:1px solid #253444;
    border-bottom:1px solid #253444;
    background:#253444;
}
.docs-material #docs-toolbar-wrapper {
    background:#253444;
}
.docs-gm #docs-toolbar-wrapper {
    border-top:0px solid #dadce0;
    border-bottom:0px solid #dadce0;
}
.docs-omnibox-input::-moz-placeholder {
    color:#9b9999;
}
.docs-gm .docs-omnibox-input.jfk-textinput {
    color:#9b9999;
    background: #253444;
}
.docs-material .goog-toolbar-button, .docs-material .goog-toolbar-combo-button, .docs-gm .goog-toolbar-combo-button, .docs-material .goog-toolbar-menu-button {
    background-color:#253444 !important;
}
.docs-gm .docs-material .goog-toolbar-button-hover, .docs-gm .docs-material .goog-toolbar-button-checked, .docs-gm .docs-material .goog-toolbar-menu-button-hover, .docs-gm .docs-material .goog-toolbar-combo-button-hover, .docs-gm .docs-material .goog-toolbar-combo-button-input, .goog-menu {
    background-color:#354454 !important;
    border-left-color:#354454 !important;
}
.docs-gm .docs-material .goog-toolbar-button-split-right .goog-toolbar-button-split-sympathy-hover, .docs-gm .docs-material .goog-toolbar-button-split-right {
    background-color:#354454 !important;
    border-left-color:#354454 !important;
}
.docs-gm .goog-toolbar-separator.goog-inline-block {
    border-left:1px solid #dadce0;
    margin:-50px auto;
    height:20px
}
.docs-gm .docs-omnibox-input.jfk-textinput {
    border: 0 solid #dadce0;
}
div.kix-page:nth-child(1) {
    background-color: rgb(100, 108, 118) !important;
}
div.kix-page:nth-child(n) > div:nth-child(2) {
    background-color: rgb(100, 108, 118) !important;
}
.kix-paginateddocumentplugin > div:nth-child(3) {
    background-color: rgb(100, 108, 118) !important;
}
#docs-chrome {
    background:#253444;
}
.kix-lineview::before {
    color: #384a53;
}
.docs-title-input {
    background: #354454;
}
.docs-gm.docs-menubar.goog-control-hover {
    background-color:#354454!important;
}
.docs-material #docs-header .docs-titlebar-buttons {
    background-color:#253444;
}
.docs-title-input {
    color:#000000;
}
.docs-gm .docs-menubar .goog-control-active,
.docs-gm .docs-menubar .goog-control:hover{
 background-color:#354454;
}

.outline-refresh:not([class*="docs-hc"]) #navigation-widget-top-shadow::after {
    background:-moz-linear-gradient(270deg, rgba(248, 249, 250, 1) 0%, rgba(0, 0, 0, 0) 100%);
    background:-webkit-linear-gradient(270deg, rgba(248, 249, 250, 1) 0%, rgba(0, 0, 0, 0) 100%);
    background:-o-linear-gradient(270deg, rgba(248, 249, 250, 1) 0%, rgba(0, 0, 0, 0) 100%);
    background:-ms-linear-gradient(270deg, rgba(248, 249, 250, 1) 0%, rgba(0, 0, 0, 0) 100%);
    background:linear-gradient(270deg, rgba(0, 0, 0, 0.26) 0%, rgba(0, 0, 0, 0) 100%);
    content:"";
    height:4px;
    left:200px;
    position:absolute;
    top:60px;
    width:455px
}
.outline-refresh .navigation-item, .outline-refresh .navigation-widget-empty-content {
    font-family:Roboto;
    color:#999ea4;
}
.outline-refresh #navigation-widget-bottom-fade {
    background:-moz-linear-gradient(rgba(255, 255, 255, 0) 0%, rgba(248, 249, 250, 1) 100%);
    background:-webkit-linear-gradient(rgba(255, 255, 255, 0) 0%, rgba(248, 249, 250, 1) 100%);
    background:linear-gradient(rgba(255, 255, 255, 0) 0%, rgb(20, 33, 47) 100%);
    bottom:0;
    position:absolute;
    transition:height 0.15s step-start, opacity 0.15s cubic-bezier(0.4, 0.0, 0.2, 1);
    width:100%
}
.outline-refresh .navigation-item.goog-button-hover {
    color:#869cdf !important;
}
.navigation-widget.outline-refresh .navigation-item .navigation-item-level-0 {
    text-align:center;
    font-size:19px;
    font-weight:500;
    padding-left:0px;
    font-weight: bolder;
    color: #7a97ed;
}
.navigation-widget.outline-refresh .navigation-item .navigation-item-level-2 {
    padding-left:40px;
    color: #7399a8;
}
.navigation-widget.outline-refresh .navigation-item .navigation-item-level-3 {
    padding-left:56px;
    color: #d3c496;
}
.navigation-widget.outline-refresh .navigation-item .navigation-item-level-1 {
    padding-left:56px;
    color:#d6b6d6;
    text-align:center;
    padding-left:0px;
}
.navigation-widget.outline-refresh .navigation-item .navigation-item-level-4 {
    color: #ca3a3a;
}
.navigation-widget.outline-refresh .navigation-item .navigation-item-level-5 {
    color: #846484;
}

.outline-refresh .navigation-item.goog-button-hover .navigation-item-level-5 {
    color:#869cdf !important;
}
.outline-refresh .navigation-item.goog-button-hover .navigation-item-level-4 {
    color:#869cdf !important;
}
.outline-refresh .navigation-item.goog-button-hover .navigation-item-level-2 {
    color:#869cdf !important;
}
.outline-refresh .navigation-item.goog-button-hover .navigation-item-level-3 {
    color:#869cdf !important;
}
.outline-refresh .navigation-item.goog-button-hover .navigation-item-level-1 {
    color:#768ccf !important;
}
.outline-refresh .navigation-item.goog-button-hover {
    padding-right:8px !important;
}
.outline-refresh .location-indicator-highlight.navigation-item {
    color:#98cfff;
}
.outline-refresh .location-indicator-highlight.navigation-item .navigation-item-level-1 {
    color:#fdcdfd !important;
}
.outline-refresh .location-indicator-highlight.navigation-item .navigation-item-level-2 {
    color:#98cfff !important;
}
.outline-refresh .location-indicator-highlight.navigation-item .navigation-item-level-3 {
    color:#eaeaba !important;
}
.outline-refresh .location-indicator-highlight.navigation-item .navigation-item-level-4 {
    color:#f65646 !important;
}
.outline-refresh .location-indicator-highlight.navigation-item .navigation-item-level-5 {
    color: #a868a8 !important;
}
  }
