css:
@-moz-document url-prefix("https://www.oreilly.com/library") {
body,
header.global .global-nav .content,
header.global .global-search .content,
.sbo-menu-top {
    background-color: #111;
}
.sbo-menu-top::before,
#sbo-reader .sbo-reader-content.sbo-sample-reader:before,
#sbo-reader .bottom-cta:before,
#sbo-reader .bottom-cta {
    border-top-color: #222;
    background-image: linear-gradient(180deg, #151515 0%, #111 100%);
}
header.global .menuList-itemsP2 .menuList-item > a,
#sbo-reader .sbo-reader-content {
    color: #888;
}
header.global .menuList-itemsP2 .menuList-subList {
    background-color: #222;
}
header.global .menuList-itemsP2 .menuList-subItem a {
    color: #888;
}
header.global .menuList-itemsP2 .menuList-subItem a:hover,
header.global .menuList-itemsP2 .menuList-subItem a:active {
    color: #AAA;
}
#sbo-reader .sbo-reader-content a,
#sbo-reader .sbo-reader-content a:link,
#sbo-reader .sbo-reader-content a:visited {
    color: #999;
    border-bottom-color: #555;
}
header.global .menuList-itemsP2 .menuList-item:hover > a {
    border-bottom-color: #888;
}
header.global .global-search .content form input[type='search'] {
    border: 1px solid #555;
}
header.global .global-search .content form input[type='search']:focus,
header.global .global-search .content form input[type='search']:active {
    border: 1px solid #888;
    outline: none;
}
.sbo-menu-top .sbo-toc-container .ss-list > h1,
#sbo-rt-content section[data-type="chapter"] > div > h1,
#sbo-rt-content section[data-type="preface"] > div > h1,
#sbo-rt-content section[data-type="appendix"] > div > h1,
#sbo-rt-content section[data-type="glossary"] > div > h1,
#sbo-rt-content section[data-type="bibliography"] > div > h1,
#sbo-rt-content section[data-type="index"] > div > h1 {
    color: #AAA;
}
#sbo-reader .sbo-reader-content h1,
#sbo-reader .sbo-reader-content h2,
#sbo-reader .sbo-reader-content h3,
#sbo-reader .sbo-reader-content h4,
#sbo-reader .sbo-reader-content h5,
#sbo-reader .sbo-reader-content h6,
#sbo-reader .sbo-reader-content .heading-1,
#sbo-reader .sbo-reader-content .paragraph-head,
#sbo-reader .sbo-reader-content .cn-chapter-number,
#sbo-reader .sbo-reader-content .heading-3,
#sbo-reader .sbo-reader-content .title_document,
#sbo-reader .sbo-reader-content .fm_title_document,
#sbo-reader .sbo-reader-content .box_title,
#sbo-reader .sbo-reader-content .chaptertitle {
    color: #AAA;
}
#sbo-reader .sbo-reader-content table {
    background-color: #111;
}
#sbo-reader .sbo-reader-content thead {
    background-color: #333;
}
#sbo-rt-content thead {
    color: #AAA;
}
#sbo-reader .sbo-reader-content tbody tr:nth-child(even) {
    background-color: #222;
}
#sbo-reader .sbo-reader-content table,
#sbo-reader .sbo-reader-content thead,
#sbo-reader .sbo-reader-content tbody tr,
#sbo-rt-content tr, #sbo-rt-content tr td {
    border-color: #333;
}
#sbo-rt-content thead td, #sbo-rt-content thead th {
    border-color: #333 !important;
}
#sbo-rt-content div.tip,
#sbo-rt-content div[data-type="tip"],
#sbo-rt-content div.note,
#sbo-rt-content div[data-type="note"] {
    border-color: #555;
}
#sbo-reader .sbo-reader-content pre,
#sbo-reader .sbo-reader-content .pre,
#sbo-reader .sbo-reader-content p.pre,
#sbo-reader .sbo-reader-content .pre1,
#sbo-reader .sbo-reader-content .pre-ex,
#sbo-reader .sbo-reader-content .pre_w {
    background-color: #222;
    border-color: #555;
    color: #888;
}
#sbo-reader .sbo-reader-content div.tip,
#sbo-reader .sbo-reader-content .tip,
#sbo-reader .sbo-reader-content .note,
#sbo-reader .sbo-reader-content div.note,
#sbo-reader .sbo-reader-content .warning,
#sbo-reader .sbo-reader-content .note1,
#sbo-reader .sbo-reader-content .sidebar1,
#sbo-reader .sbo-reader-content .boxg,
#sbo-reader .sbo-reader-content div.sidebar,
#sbo-reader .sbo-reader-content div.warning {
    border-color: #555;
    background-color: #181818;
    box-shadow: #333 0px 4px 5px
}
#sbo-reader .sbo-reader-content img {
    filter: invert(0.935) hue-rotate(180deg);
    opacity: .5;
}
#sbo-rt-content div.figure h6 {
    color: #888;
}
#sbo-reader .bottom-cta a.button-primary {
    color: #fff;
}
footer {
    background-color: #222;
}
footer:after {
    opacity: 0.075;
}
}
