css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gentoo.ru") {
body {
	background: #515457 !important;
	color: #fff !important;
	font-family: sans-serif !important;
}
h1, h2, h3 {
	color: #fff !important;
}
h2.title a {
	color: #fff !important;
	text-decoration: none !important;
}
td#logo {
	display: none
}
a:link { 
	color: #6f6 !important;
}
a:visited { 
	color: #3c3 !important;
}
#mission {
	background: #515457 !important;
}
#main .node .title {
	border-bottom: 1px solid #ccc !important;
}
#main .sticky {
	background: #545751 !important;
	border: 1px solid #222 !important;
}
#sidebar-left, #sidebar-right {
	background: #515457 !important;
	padding: 4px !important;
}
#sidebar-left .block, #sidebar-right .block {
	border: none !important;
}
#sidebar-left .title, #sidebar-right .title {
	border-bottom: 1px solid #ccc !important;
}
#content {
	table-layout: fixed
}
#footer {
	background: #549 !important;
}
.block-forum ul, .block-blog ul {
	font-size: 0.8em !important;
	padding-left: 0 !important;
}
span.marker {
	color: #f66 !important;
	font-size: 0.7em !important;
}
table.sticky-header {
	display: none
}
#tracker td {
	font-size: 0.7em !important;
}
#tracker td:first-child + td {
	font-size: 1em !important;
}
th {
	background: #3e403b !important;
	border: solid 1px #888 !important;
	padding: 0.2em !important;
}
tr.odd, tr.even {
	background: #545751 !important;
	border: none !important;
	padding: 0.1em !important;
}
tr.odd td, tr.even td {
	border: solid 1px #888 !important;
}
#forum tr.odd td, .gbEven {
	background: #545751 !important;
}
#forum tr.even td, .gbOdd {
	background: #60605c !important;
}
#forum tr td.container {
	background-color: #444 !important;
}
.indented {
	margin-left: 1em !important
}
.comment {
	background: #545751 !important;
	border: 1px solid #222 !important;
	padding: 4px !important;
	margin-bottom: 0.5em !important
}
.comment .title {
	background: #3E403B !important;
	border-bottom: 1px solid #222 !important;
	font-size: 0.9em !important;
	font-weight: bold !important;
	margin: -4px !important;
	margin-bottom: 0 !important;
	padding: 0.1em 0.5em !important
}
.comment .title a {
	color: #fff !important;
	font-weight: bold !important;
}
.comment span.new {
	color: #f66 !important;
}
.content pre {
	background-color: #666 !important;
	border: 1px solid #777 !important;
	margin: 0 !important;
	white-space: pre !important;
	overflow-x: auto;
}
.content code {
	color: #99f !important;
}
.content code.bb-code {
	color: #9ff !important;
}
.bb-quote-body {
	background-color: #665 !important;
	border: 1px solid #776 !important;
	margin: 0 !important;
}
.content .clear-block {
	color: #999 !important;
}
#comment-controls .description {
	display: none
}
.tabs li.active a {
	background: #676 !important;
	color: #fff !important;
	border-bottom: none !important;
}
.tabs li a {
	background: #555 !important;
}
label {
	color: #ccc !important;
}
.pager {
	margin-top: 0.4em  !important;
}
.pager li {
	margin: 0  !important;
	padding: 0 !important;
}
.pager a, .pager li.pager-current {
	border: 1px solid #777 !important;
	margin: 0  !important;
	padding: 0 0.5em !important;
	text-decoration: none !important;
}
.pager a:hover {
	background: #444 !important;
}
.gecko {
	background: #545751 !important;
	border: solid 1px #888 !important;
	color: #fff !important;
}
.gcBackground1, .gcBackground2 {
	background: none !important;
}
}
