}

.comment-text {
    background-color: #0f0f0f;
    max-width: 30.25rem;
}

.comment-text {
    position: relative;
    border: 1px solid #494949;
    border-radius: 0 5px 5px 5px;
    padding: 1rem;
}

.comment-text:before {
    display: block;
    position: absolute;
    top: -1px;
    left: -13px;
    border-bottom: 12px solid #494949;
    border-left: 13px solid #494949;
    border-radius: 0 0 0 13px;
    width: 0;
    content: "";
}

.comment-text:after {
    display: block;
    position: absolute;
    top: 0;
    left: -12px;
    border-bottom: 10px solid #0f0f0f;
    border-left: 12px solid #0f0f0f;
    border-radius: 0 0 0 12px;
    width: 0;
    content: "";
}

.button {
    display: inline-block;
    margin: .5em 0;
    border: 0;
    border-radius: .5rem;
    background-color: #001635;
    cursor: pointer;
    padding: 1em 1.25em;
