css:
body {
    background-color: #121212;}
#layout-menu-cover {
    background-color: #000000;
}
#layout-menu-cover {
    border-right: solid 1px #4e6977;
}
.Separator {
    border-bottom: solid 1px #4e6977;
}
ul.ui-tabs-nav {
    background-color: #4e6977 !important;
}
.layout-menu li a {
    color: #85A314;
}
#layout-menu-cover ul.active-menu {
    background-color: #000000;
}
.layout-menu > li > a.active-menu {
    background-color: #000000 !important;
}
li.leftMenuItem > a {
    background-color: #000000;
}
.layout-menu a.leftMenuSubItem {
    background-color: #000000;
}
.TopInfoBar {
    background-color: #000000;
}
.ProfileName {
    color: #78909c;
}
.ui-tabs-top .ui-tabs-nav li.ui-tabs-selected, .ui-tabs-top .ui-tabs-nav li.ui-tabs-active {
    background-color: #000000 !important;
}
.ui-tabs-top .ui-tabs-nav li {
    background-color: #000000 !important;
}
.ui-corner-all, .ui-corner-top, .ui-corner-right, .ui-corner-tr {
    border-top-right-radius: 0px;
}
.ui-corner-all, .ui-corner-top, .ui-corner-left, .ui-corner-tl {
    border-top-left-radius: 0px;
}
.Card {
    background-color: #000000 !important;
}
input.ui-inputfield {
    color: #498dae;
}
.ui-inputfield {
    border-bottom: solid 1px #4e6977;
}
.ui-datagrid {
    background-color: #000000 !important;
}
.Charcoal {
    color: #78909c !important;
}
div.ui-selectonemenu label.ui-selectonemenu-label {
    background-color: #000000 !important;
    color: #498dae;
}
.ui-selectonemenu .ui-selectonemenu-trigger, .ui-selectcheckboxmenu .ui-selectcheckboxmenu-trigger {
    padding: 0px 4px !important;
    background-color: #000000 !important;
     border-bottom: solid 1px #4e6977 !important;
}
.ui-selectonemenu, .ui-selectcheckboxmenu {
    border-bottom: solid 1px #4e6977 !important;
}
table {
    background-color: #000000;
}
.ui-panel {
    background-color: #6213d7;
}
.ListItemName {
    color: #78909c;
    font-size: 16px;
}

