css:
@-moz-document domain("ravelry.com") {
a.page_bar__page {
    box-shadow: 0px 0px 0 0;
}

a,
a.reaction_button,
a:link,
a:visited {
    font-family: verdana;
    color: #bdbdbd;
}

td.title {
    color: #bdbdbd;
}

h2.breadcrumbs {
    background-color: #2e385b;
}
.navigation_v2__tab,
.navigation_v2__tab:visited,
.navigation_v2__tab:hover {
    background-color: #2e385b;
}
.navigation_v2__spacer {
    background-color: #2e385b;
}
.navigation_v2__notification,
.navigation_v2__notification__wrapper {
    background-color: #2e385b;
}
.navigation_v2__logo {
    background-color: #2e385b;
}

.navigation_v2,
.linkbar {
    background-color: #2e385b;
}

.c-navigation_dropdown__column {
    background-color: #181d2f;
}
.c-navigation_dropdown__content a,
.c-navigation_dropdown__content a:visited {
    color: #bdbdbd;
    background-color: #181d2f;
}
.c-navigation_dropdown__content a:hover {
    color: #bdbdbd;
    background-color: #2e385b !important;
}
.c-navigation_dropdown__content_wrapper {
    background-color: #181d2f;
    border: 1px solid #000;
}
.topics .forum_posts .reply .voting a.marked {
    color: #bdbdbd;
}

.fancy_form label,
.vertical_form label {
    display: block;
    float: none;
    font-size: 1em;
    padding: 0.5em 0;
    color: #bdbdbd;
    width: 320px;
}

.clicker_v2 {
    background-color: #181d2f;
    border-radius: 5px;
    box-shadow: 0px 0px 0 0;
    color: #bdbdbd !important;
}
.clicker_v2:hover {
    background-color: #2e385b;
}
.forum_post_listing .forum_post_header {
    border: 1px solid #000;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    text-align: center;
}
.forum_post_listing .forum_post_row {
    border: 1px solid #000;
    border-top: none;
}

#forum_posts {
    background-color: #191e31;
}

div.body.forum_post_body {
    width: 100%;
    margin: 0 auto;
}

a.post_number {
    float: right;
}
div.reply.forum_post__reply_tools {
    width: 100% !important;
    margin: auto;
}

.forum_post_body blockquote {
    background-color: #202331;
    max-width: 100%;
}


body.topics_index .pagination a,
.page_links .pagination a,
.page_buttons a {
    border: 1px solid #000;
    background-color: #2e385b;
}
body.topics_index .pagination a:hover,
.page_links .pagination a:hover,
.page_buttons a:hover {
    background-color: #181d2f;
}
body.supports_subnavigation.with_subnavigation .breadcrumbs__crumb a {
    color: #000;
    font-size: 14px;
}
.page_bar__current {
    border: 1px solid #000;
    margin-right: 4px;
    background-color: #2e385b;
    font-weight: bold;
}
table#forum_posts thead td,
table.grid th,
table.grid tr.header td {
    background-color: #2e385b;
}

table.reply_list a,
.forum_glance table a {
    color: #bdbdbd !important;
}

.topics #topic_toolbar {
    background-color: #2e385b;
    color: #bdbdbd;
}
.topics #topic_toolbar a,
.topics #topic_toolbar a:visited {
    color: #000;
}
#topic_toolbar .title_overlay a {
    color: #000 !important;
    font-weight: bold;
}
.box_title,
.box_title--standalone {
    background-color: #2e385b;
    border: 1px solid #000;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
}
.box_contents,
tr,
#facets {
    background-color: #181d2f;
    border-bottom-left-radius: 5px;
    border-bottom-right-radius: 5px;
}

div#facets.advanced_search_facets {
    background-color: #181d2f;
}

.box {
    box-shadow: 0px 0px 0 0 #000;
}
.resizable_table__cell-header {
    background-color: #e7f7e1;
}
body.with_navigation_v2 #footer {
    background-color: #2e385b;
}
body.with_navigation_v2 #footer a,
body.with_navigation_v2 #footer a:visited {
    color: #bdbdbd;
}
#footer {
    color: #bdbdbd;
}
body.search .advanced_search__facets {
    background-color: #2e385b;
}
.forum_post_drafts__post {
    box-shadow: 0px 0px 0 0 #000;
}
/* New code FIX YELLOW ROLL OVER*/
.discussions #reply_status table.reply_list {
    box-shadow: 0px 0px 0 0 #000;
}

body,
ol,
ul,
td,
th,
input,
select,
table,
a.login,
.time,
.irt {
    font-family: verdana;
    font-size: 1rem;
    color: #bdbdbd;
}

p,
li {
    line-height: 100%;
    max-width: 100%;
}

#page {
    background-color: #202331;
}

.forum_glance table tr.hover {
    background-color: #fffff2;
    cursor: pointer
}

#topics table,
.forum_glance table {
    border-bottom: 0px solid #000;
}

#topics table th,
#topics table tr:first-of-type td,
.forum_glance table th,
.forum_glance table tr:first-of-type td {
    border-top: 1px solid #000;
}
#topics table,
.forum_glance table {
    width: 100%;
    border-collapse: separate;
    border-spacing: 0;
    border-bottom: 1px solid #000;
    background-color: transparent;
}
.forum_glance table td:first-of-type {
    border-left: 1px solid #000
}
#topics table th:last-of-type,
#topics table td:last-of-type,
.forum_glance table th:last-of-type,
.forum_glance table td:last-of-type {
    border-right: 1px solid #000
}
#topics table th,
#topics table tr:first-of-type td,
.forum_glance table th,
.forum_glance table tr:first-of-type td {
    border-top: 1px solid #000
}
#topics table tr:last-of-type td,
.forum_glance table tr:last-of-type td {
    border-bottom: 1px solid #000
}
#topics table th:first-of-type,
#topics table td:first-of-type,
.forum_glance table th:first-of-type,
.forum_glance table td:first-of-type {
    border-left: 1px solid #000;
}
body.normal,
#page_header {
    background-color: #202331;
}
body.supports_subnavigation.with_subnavigation .tabs--reveal_overflow,
body.supports_subnavigation.with_subnavigation--all.notebook .tabs--reveal_overflow,
body.supports_subnavigation.with_subnavigation.discussions_navigation .tabs--reveal_overflow {
    background: -webkit-linear-gradient(top, #fbfbfb 0%, #fafafa 4%) 0 -2px;
    background: linear-gradient(top, #fbfbfb 0%, #fafafa 4%) 0 -2px;
}

body.supports_subnavigation.with_subnavigation .tabs--dark {
    background-color: #1A444D !important;
    border-bottom-color: #1A444D !important;
}
body.supports_subnavigation.with_subnavigation .tabs--subnavigation {
    margin-top: 0;
    padding-top: 0;
    top: 1px;
    margin-bottom: 32px !important;
    border: none;
    background-color: #fbfbfb;
    border-bottom: 1px solid #000
}

.hopper {
    color: #000;
}

.title,
.login {
    font-family: bookman;
    color: #bdbdbd;
}
div.tabs__sections.tabs__sections--subnavigation {
    background-color: #2e385b;
    border: 1px solid #000;
}

body.topics_show .parent_post {
    margin-left: 5px;
    padding: 10px;
    margin-bottom: 10px;
    color: #bdbdbd;
    border: 1px solid #000;
    background-color: #2e385b !important;
    max-width: 100%;
}
.tabs ul li a {
    color: #bdbdbd !important;
    white-space: normal;
}

div.forum_glance a {
    color: #bdbdbd !important;
}

.navigation_v2__tab--dropdown > a,
.navigation_v2__tab--dropdown > a:visited,
.navigation_v2__tab--dropdown > a:hover {
    color: #bdbdbd;
    text-decoration: none;
}

body.supports_subnavigation.with_subnavigation .breadcrumbs__crumb a {
    color: #bdbdbd;
    font-size: 14px;
}
.tabs--subnavigation li .c-navigation_indicator__snake {
    border-bottom-width: 4px;
    border-color: #000;
}
body.search .advanced_search__facet_content {
    background-color: #202331;
    border: 1px solid #000;
}
.facet_box li.value a {
    color: #bdbdbd;
}
.facet_box li input {
    color: #bdbdbd;
}

.facet_box li.value a:hover {
    color: #bdbdbd;
    background-color: #2e385b;
}

#topic_toolbar .title_overlay a {
    color: #bdbdbd !important;
    font-weight: bold;
}

.topics #topic_toolbar a,
.topics #topic_toolbar a:visited {
    color: #bdbdbd;
}

#topic_tools .box_title {
    background-color: #2e385b;
    border-bottom: none;
}
.form_select.small_170_with_icon select,
.fake_form_select.small_170_with_icon select {
    border-left: 21px solid transparent;
    color: #000;
}
body.search #search_navigation {
    background-color: #2e385b;
}
body.search #search_navigation li a,
body.search #search_navigation li a:visited {
    color: #bdbdbd;
}
body.search .advanced_search__filter_jump .form_select,
body.search .advanced_search__filter_jump select {
    color: #000;
}
.facet_box li {
    font-size: 0.9rem;
}
.navigation_v2--advanced_search .c-navigation_indicator__snake {
    border-bottom-color: #000;
    margin-top: 0 !important;
}

ul.page_date_sidebar a {
    color: #bdbdbd;
}
body.supports_subnavigation.with_subnavigation .breadcrumbs__crumb--active,
body.supports_subnavigation.with_subnavigation .breadcrumbs__crumb--active a,
body.supports_subnavigation.with_subnavigation .breadcrumbs__crumb--active a:hover {
    color: #bdbdbd;
}

ul.tag_set li.tag,
span.tag_word {
    border-style: solid;
    cursor: pointer;
}
ul.tag_set li:not(.tag_set__control),
span.tag_word {
    border-radius: 5px;
    border-color: #000;
    background-color: #181d2f;
}

a.button {
    border-radius: 5px;
    border: 1px solid #000 !important;
    background-color: #181d2f;
    font-size: 0.85rem !important;
    width: 45% !important;
}
.core_item_panel #button_box .button:hover {
    border-radius: 5px !important;
    border: 1px solid #000 !important;
    background-color: #2e385b !important;
}
.yarns #button_box .button:hover {
    border-radius: 5px !important;
    border: 1px solid #000 !important;
    background-color: #2e385b !important;
}

div#button_box {
    background-color: #202331;
}
ul.tag_set li:not(.tag_set__control) a {
    color: #bdbdbd;
}

#activities blockquote,
.forum_post_body blockquote,
.markdown blockquote,
body.search_discussions blockquote {
    background-color: #2e385b;
}
.bar_rating li.filled {
    background-color: #4f5f9d;
}
.resizable_table__header {
    width: 100%;
    display: table-header-group;
    background-color: #2e385b;
}

.download_plate {
    overflow: hidden;
    border: 1px solid #000;
    box-shadow: 0px 0px 0 0 #000;
    background-color: #181d2f;
    border-radius: 5px;
}

div.chat_status a {
    color: #bdbdbd !important;
}
.popover_list li a {
    color: #bdbdbd;
}
.notebook_thumbnails a:visited,
.notebook .thumbnail a:visited {
    color: #bdbdbd;
}
legend {
    color: #bdbdbd;
}
}
