css:
@-moz-document url-prefix("https://www.gram.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 5px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 5px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #f00;
        }

body, .carousel-indicators div, .progress-bg-not-loaded, #cookie-box-container-inner, a.button.secondary, button.secondary, a.button.contrast, button.contrast, .tag, .content-list .news, .rating-container, .comment-counter, .header .submenu-pc, .header .submenu-pc a, .header .submenu-pc button, .header .submenu-pc p, .header .submenu-pc span, .input-wrapper, .input-wrapper, input, textarea, .form-container, .dialog, .comments .c-div, .comments blockquote, .comments .top .count {
    background: #111 !important;
    border-color: #222 !important;
    color: #eee;
}
.tabs .tab-button, .tabs .tab-button:disabled, .content-list .news .miniature {
    background: #111;
    color: #eee;
    border-color: #222;
}
.tabs .tab-button.selected {
    border-bottom-color: #ca1527;
}
.tabs .tab-button:hover {
    color: #eee;
    background-color: #111;
    border-bottom-color: #ffbd565e !important;
}
.apply-paddings, .articles-room {
    background: #151515 !important;
    border-color: #222 !important;
}
.row-border, .newest-articles article, article, .column-border, article .na-content img.inner, #cookie-box-container-inner #cookie-scrollable-text, .content-list .news .line, .tabs .just-grey-line, .tabs .tab-button.grey, .tabs .tab-button.grey:disabled {
    border-color: #222 !important;
}
.newest-articles article h2 {
    color: #fff;
}
article ul li, .content-top-bar .date, .content-list .news:hover .title {
    color: #ccc;
}
.content-list .news .date, .comments .comment .top .date, .comments blockquote {
    color: #bbb;
}
.meta {
    background: #151515 !important;
}
.newest-articles article {
    background-color: #000000cf
}
.content-top-bar .content-widgets .divider, .divider {
    background: #222;
}
main.content > p > strong > span, main.content > p > strong > em > span {
     background: #222 !important;
}
.comment-counter:after {
    border-top-color: #111;
}
.comment-counter:before {
    border-top-color: #222;
}
a.button.secondary:hover, a.button:hover, button.secondary:hover, button:hover {
    background-color: #ffdca79c;
}
a.button.light, button.light, a.link, button.link {
    color: #eee;
}
a.share-button {
    color: #444;
}
a:hover.share-button.tw {
    background-color: #2795e9;
    color: #fff;
}
.home .tile {
    background-color: #222;
}
input[type=checkbox]:disabled:before {
    background: #222;
}
input[type=checkbox]:before {
    background: #111;
}
}
