css:
@-moz-document url-prefix("https://www.baidu.com/s") {
::selection {
    color: #fff !important;
    background: rgba(128, 210, 106, 0.4) !important;
}

/**背景*/
/* 未访问的链接 */
body {
    /*     background: url(https://i.loli.net/2020/06/13/oLxPXIeSZGyjEvM.png); */
    background: url(https://s1.ax1x.com/2020/07/14/UNr6BD.jpg);
    background-attachment: fixed;
    background-size: cover;
}

/* 取消页面加载时的白色遮罩 */
#_mask {
    opacity: 0 !important;
}

.wrapper_new #head {
    height: 70px !important;
}

#head {
    height: 55px !important;
}

/* 取消头部边框 */
body #head {
    border-bottom: 0
}

/* 不下拉头部字体颜色 */
#head:not(.s_down) #u a {
    color: rgb(145, 149, 163) !important;
}

/* 下拉头部字体颜色 */
#head.s_down #u a {
    color: rgba(128, 210, 106, 1) !important;
}

/* 网页，资讯，视频 */
.wrapper_new #s_tab .cur-tab:after {
    background: rgb(40, 173, 244);
}

.wrapper_new #s_tab a {
    color: rgb(69, 198, 41);
}

.wrapper_new #s_tab .s-tab-item:before {
    color: rgb(47, 255, 215);
}

.wrapper_new #s_tab .s-tab-item:hover:before {
    color: rgb(40, 173, 244);
}

.wrapper_new #s_tab .s-tab-item:hover {
    color: rgb(40, 173, 244);
}

.wrapper_new #s_tab .cur-tab {
    color: rgb(40, 173, 244);
}

/* 头部背景：不下拉 */
#head:not(.s_down) {
    background: rgba(0, 0, 0, .1) !important;
    box-shadow: 0 5px 10px rgba(0, 0, 0, .1) !important;
    transition: all 0.5s
}

/* 头部背景：下拉 */
#head.s_down {
    background: rgba(0, 0, 0, .3) !important;
    box-shadow: 0 5px 10px rgba(0, 0, 0, .1) !important;
    transition: all 0.5s;
    top: 0 !important;
}

.nums {
    font-size: 12px;
    color: rgba(128, 210, 106, 1);
}

.wrapper_new #u .username {
    margin-left: 24px;
    margin-top: 16px;
    display: inline-block;
    height: 30px;
}

.wrapper_new #head {
    position: fixed !important;
}

.wrapper_new #u {
    margin: 5px 0 0 0 !important;
}

#u {
    margin: 21px 0 0 0 !important;
}

/* 头部下面小字背景透明 */
#s_tab {
    border-bottom: none;
    background: transparent;
}

/*百度识图图标透明*/
.soutu-btn {
    background-color: transparent;
    top: 52%;
}

.wrapper_new #result_logo {
    margin: 15px 0 0;
}

.s_btn_wr {
    position: relative;
}

/*搜索框*/
.wrapper_new .fm {
    margin: 20px 0 20px 19px;
}

.wrapper_new #head.fix-head #result_logo {
    margin-top: 15px;
}

.wrapper_new #head.fix-head .fm {
    margin: 20px 0 15px 19px;
}

.wrapper_new #kw.s_ipt {
    height: 36px;
    font: 16px/18px arial;
    padding: 0 0 3px 14px !important;
    background: transparent;
    border: 0;
    outline: 0;
    -webkit-appearance: none;
}

.s_ipt {
    background: transparent;
    margin: 4px 0 0 7px
}

.wrapper_new #head.fix-head #kw {
    height: 36px;
}

.wrapper_new .s_ipt_wr {
    border-radius: 8px 0 0 8px !important;
    background: rgba(255, 255, 255, 0.3) !important;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
    transition-duration: 0.5s;
    height: 34px !important;
    /* 字体颜色 */
}

.s_ipt_wr {
    border-radius: 8px 0 0 8px !important;
    background: rgba(255, 255, 255, 0.3) !important;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
    transition-duration: 0.5s;
    height: 30px !important;

}

/*搜索框small*/
.wrapper_s .s_ipt_wr {
    width: 454px !important;
}

/* 搜索下拉框small */
.wrapper_s .bdsug {
    width: 454px !important;
}

/* 搜索下拉框：键盘移动small */
.wrapper_s .bdsug li.bdsug-s {
    width: 439px !important;
}

/*搜索下拉框：鼠标划过small*/
.wrapper_s .bdsug-overflow:hover {
    width: 439px !important;
}

/*搜索框large*/
.wrapper_l .s_ipt_wr {
    width: 564px !important;
}

/* 搜索下拉框large */
.wrapper_l .bdsug {
    width: 564px !important;
}

/* 搜索下拉框：键盘移动large */
.wrapper_l .bdsug li.bdsug-s {
    width: 549px !important;
}

/*搜索下拉框：鼠标划过large*/
.wrapper_l .bdsug-overflow:hover {
    width: 549px !important;
}

/*搜索框输入字体*/
.s_ipt_wr input {
    color: rgb(0, 0, 0) !important;
}

/*搜索框：鼠标划过*/
.s_ipt_wr:hover,
.s_ipt_wr.ipthover {
    background: rgba(255, 255, 255, 0.3) !important;
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
    border: 2px solid rgba(128, 210, 106, 0.6);
}

/*搜索框：鼠标点击*/

.wrapper_new .iptfocus.s_ipt_wr {
    background: rgba(255, 255, 255, 0.7) !important;
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1) !important;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
}

.wrapper_new #form .bdsug-new ul {
    margin: 5px 0 0;
    padding: 0px 0 7px;
    background: 0 0;
    border-top: none;
}

.wrapper_new #form .bdsug-new ul li {
    padding-left: 14px;
}

.wrapper_new #form .bdsug-new {
    top: 36px;
    border-radius: 0 0 10px 10px;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
    /* border-top: 0!important; */
    box-shadow: none;
    font-family: 'Microsoft YaHei', Arial, sans-serif;
    z-index: 1;
}


/* 搜索下拉框：键盘移动 */
.bdsug li.bdsug-s {
    background: rgba(100, 220, 250, 0.2) !important;
    box-shadow: 0px 0px 15px rgba(100, 220, 250, 1) !important;
}


/* 搜索下拉框 */
.bdsug {
    border-image-width: 50px !important;
    margin-top: 1px;
    background: rgba(255, 255, 255, 0.7) !important;
    border: 2px solid rgba(128, 210, 106, 1) !important;
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1) !important;
    border-radius: 8px 8px 8px 8px !important;
}

.wrapper_new .fix-head.s_down #form .bdsug-new {
    top: 35px;
}

/* 搜索框关键字字体 */
.bdsug-overflow {
    color: rgb(0, 136, 2) !important;
}

/* 搜索框字体 */
.bdsug-overflow b {
    color: rgb(0, 0, 0) !important;
    font-weight: unset;
}

/* 搜索下拉框：鼠标划过 */
.bdsug-overflow:hover {
    background: rgba(100, 220, 250, 0.2) !important;
    box-shadow: 0px 0px 15px rgba(100, 220, 250, 1) !important;
}



.bdsug-feedback-wrap {
    background: transparent !important;
}

/*“百度一下”按钮*/
.wrapper_new .s_btn {
    left: -1px;
    width: 112px;
    font-size: 17px;
    border-radius: 0 8px 8px 0 !important;
    background: rgba(128, 210, 106, 0.6) !important;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
    border-left: none !important;
    transition-duration: 0.5s;
    position: absolute;
    color: rgb(255, 255, 255) !important;
    height: 38px !important;
    line-height: normal !important;
}

.s_btn {
    left: -1px;
    width: 112px;
    font-size: 17px;
    border-radius: 0 8px 8px 0 !important;
    background: rgba(128, 210, 106, 0.6) !important;
    border: 2px solid rgba(128, 210, 106, 0.6) !important;
    border-left: none !important;
    transition-duration: 0.5s;
    position: absolute;
    color: rgb(255, 255, 255) !important;
    height: 34px !important;
    line-height: normal !important;
}

/*“百度一下”按钮：鼠标划过*/
.s_btn.btnhover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1) !important;
    ;
}

/* 搜索框下面的选项 */
#s_tab a {
    color: rgb(145, 149, 163);
}

/* 搜索框下面的选项：鼠标划过 */
#s_tab a:hover {
    color: rgba(128, 210, 106, 1);
}

/* 搜索框下面的选项：选中状态 */
#s_tab b {
    border-bottom: 2px solid rgba(128, 210, 106, 1);
    color: rgba(128, 210, 106, 1);
}


/* 搜索工具 */
/* 找到相关资讯 */
#header_top_bar {
    margin: 12px 0 0 18px;
}

@media screen and (min-width: 1921px) {
    #header_top_bar {
        width: 1212px;
        margin: 12px auto 0 auto;
        padding-left: 242px;
        -webkit-transform: translate3d(-46px, 0, 1px);
        transform: translate3d(-46px, 0, 1px);
    }
}

#header_top_bar .nums {
    color: rgba(128, 210, 106, 1);
}

.head_nums_cont_inner {
    margin-left: 11px;
    margin-top: 10px;
}

.search_tool,
.search_tool_conter span {
    color: rgb(145, 149, 163);
}

.search_tool:hover,
.search_tool_conter span:hover {
    color: rgba(128, 210, 106, 1);
}

#header_top_bar .search_sort_news {
    color: rgb(145, 149, 163);
}

#header_top_bar:hover .search_sort_news:hover {
    color: rgba(128, 210, 106, 1);
}

/* 主体 */
/* #container, */
#content_left {
    border: 2px solid rgba(128, 210, 106, 1) !important;
    border-radius: 10px 10px 10px 10px !important;
    width: 540px !important;
    padding: 15px 10px 0 15px !important;
    margin: 20px 0 0 121px !important;
    background: rgba(255, 255, 255, .5) !important;
}

.wrapper_new #content_left {
    margin: 20px 0 0 140px !important;
}

#content_left:hover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
}

.c-recommend {
    border: 1px solid rgba(128, 210, 106, 1);

}

.op_dict3_extra {
    border-top: 1px solid rgba(128, 210, 106, 1);
    padding-top: 13px;
    margin-top: 13px;
}

/* 去网页搜索 */
#gotoPage {
    clear: both;
    display: block;
    float: left;
    background: rgba(255, 255, 255, .5);
    margin: 10px 0 10px 121px;
    padding: 10px 0 10px 20px;
    border: 2px solid rgba(128, 210, 106, 1);
    border-radius: 10px 10px 10px 10px;
    width: 545px;
}

#gotoPage:hover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
}

/* 底部相关搜索 */
#rs {
    background: rgba(255, 255, 255, .5);
    width: 558px;
    display: block;
    margin-top: 10px;
    padding: 5px 2px 0 5px;
    border: 2px solid rgba(128, 210, 106, 1);
    border-radius: 10px 10px 10px 10px;
    clear: both;
}

@media screen and (min-width: 1921px) {
    #wrapper>#rs {
        position: relative;
        box-sizing: content-box;
        left: -200px;
    }
}

#rs:hover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
}

#rs div.tt {
    margin: 0 0 0 12px;
    padding: 6px 0 0 0;
}

#rs table {
    display: inline-block;
    margin: 0 9px 5px 10px;
}


/* 底部页码跳转：网页格式 */
.wrapper_new .container_new~#page {
    background: rgba(255, 255, 255, 0);
}

.wrapper_new .container_new~#page .page-inner {
    background: rgba(255, 255, 255, .5);
    padding: 14px 10px 14px 22px;
    border: 2px solid rgba(128, 210, 106, 1);
    border-radius: 10px 10px 10px 10px;
    width: max-content;
    position: relative;
    left: 140px;
}

.wrapper_new .container_new~#page strong,
.wrapper_new .container_new~#page a {
    width: 36px;
    height: 36px;
    border: 0;
    border-radius: 6px;
    background-color: rgba(255, 255, 255, .0);
    color: #3951B3;
    margin-right: 12px;
}

.wrapper_new .container_new~#page strong {
    background: rgba(128, 210, 106, 0.6);
}

.wrapper_new .container_new~#page a.n {
    margin-bottom: 0 !important;
}

/* 底部页码选中变色 */
.wrapper_new .container_new~#page a:hover,
.wrapper_new .container_new~#page a:hover .pc,
.wrapper_new .container_new~#page .n:hover {
    background: rgba(128, 210, 106, 0.5) !important;
    box-shadow: 0px 0px 15px rgba(100, 220, 250, 1) !important;
    z-index: 999 !important;
    color: #fff;
}

.wrapper_new .container_new~#page .page-inner:hover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
}

@media screen and (min-width: 1921px) {
    .wrapper_new .page-inner {
        left: -162px !important;
    }
}

/* 底部页码跳转：资讯格式 */
p#page {
    background: rgba(255, 255, 255, .5);
    padding: 0 20px 0 20px;
    margin-top: 10px;
    width: max-content;
    border: 2px solid rgba(128, 210, 106, 1);
    border-radius: 10px 10px 10px 10px;
    position: relative;
    left: 121px;
}

/* 消除猫爪图标 */
p#page .c-icon {
    background: transparent;
}

/* 页码距离顶部的空白 */
p#page .fk {
    width: 0;
    height: 0;
    margin-bottom: 11px;
}

/* 上一页，下一页 */
p#page a.n {
    margin-bottom: 10px !important;
    border: 0;
    border-radius: 6px;
    color: #3951B3;
    padding: 0 11px;
}

/* 包含页码的框 */
p#page a,
p#page strong {
    background: transparent;
    height: 56px;
    padding: 0 6px;
    margin: 0;
}

p#page strong {
    font-weight: normal;
}

/* 页码：非当前 */
p#page .pc {
    border: 0;
    border-radius: 6px;
    color: #3951B3;
    width: 34px;
    height: 34px;
    line-height: 34px
}

/* 页码：当前 */
p#page strong .pc {
    background: rgba(128, 210, 106, 0.5);
    border: 0;
    border-radius: 6px;
    color: #3951B3;
    width: 34px;
    height: 34px;
}

p#page .n:hover,
p#page a:hover .pc {
    background: rgba(128, 210, 106, 0.5);
    border: 0;
    border-radius: 6px;
    box-shadow: 0px 0px 8px rgba(100, 220, 250, 1) !important;
    color: white;
}

p#page:hover {
    box-shadow: 0px 0px 20px rgba(100, 220, 250, 1);
}

@media screen and (min-width: 1921px) {
    p#page {
        left: -261px !important;
        box-sizing: content-box;
        width: 525px;
    }
}

/* 底部横条 */
#foot {
    background: transparent;
    border: none;
}

.wrapper_new #foot {
    background: transparent;
    border-top: none;
}

/* 底部横条help */
#help,
.wrapper_new #foot #help {
    background: transparent;
    clear: both;
}

/* 底部横条字体颜色 */
#help a {
    color: rgb(145, 149, 163);
}

#help a:hover {
    color: rgba(128, 210, 106, 1);
}

.wrapper_new #foot a {
    color: rgb(145, 149, 163);
}

.wrapper_new #foot a:hover {
    color: rgba(128, 210, 106, 1);
}
}
