css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("digital-tunes.net") {

body {
  background:#fff !important;
  margin:0 !important;
  padding:0 !important;
}

#header {
  position:relative !important;
  height:auto !important;
  font-weight:normal !important;
  padding:0 !important;  
}

a.no-hover[target=_blank] img { display:none !important; } 

td, p, a{
  font-size:12px !important;
}


h1, h2, h2 a, .h2, h1.left {
  text-align:left !important;
  font-size: 15px !important;
}

h1 {
 font-size:16px !important;
}

#logo {
  height:40px !important;
}

.status-icon {
 width:25px !important;
}

.status-table{
  width:auto !important;
}

#breadcrumb-trail, a.breadcrumb {
  font-size:14px !important;
}

@media screen{
 body>#header{
    position : fixed;
 }
}


#content {
  padding:0 !important;
}


#footer{
  font-size:10px !important;
}


.basket-info, .wishlist-info {
  font-size:12px !important;  
}







#subsearch-text{
  color : #a0d400;
  font-size : 15px;
 }

#subsearch-input{
  border : 1px dotted #a0d400;
  font-size : 13px;

}

#search-input{
  width:220px !important;
  color:#000 !important;
  font-size:13px !important;
}

.search-button{
  border:0px !important;
  line-height:26px;
  cursor: pointer;
}



#footer-links {
 padding-top:3px !important;
}

.footer {
  margin:2px 5px !important;
}

.lang-selector img {
 height:15px !important;
}




.column-header{
  position : relative;
  width : 25em;
  height : 1.2em;
 }




/*----*/


.menu {
  width:auto !important; 
  height:auto !important; 
  position:relative !important; 
  font-size:14px !important;
}

.menu ul li a, .menu ul li a:visited {
  width:100px !important; 
  height:20px !important;  
  line-height:18px !important; 
  font-size:12px !important;
}

.menu li li a, .menu li li a:visited {
  width:125px !important;
  font-size:12px !important;
}

.menu ul li:hover ul {
  width:125px !important;
  top:22px !important;  
}

.menu ul li:hover ul li:hover ul      { left:127px; top:0px !important; }

}
