css:
@-moz-document domain("eurogamer.pl"), domain("eurogamer.cz"), domain("eurogamer.es"), domain("eurogamer.it"), domain("eurogamer.net"), domain("eurogamer.nl"), domain("eurogamer.pt") {
html,
body {
	background: #1c1c1c;
	color: #fff;
}
::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
	border-radius: 5px;
	background-color: #1c1c1c;
}
html[hide-scrollbar="true"] ::-webkit-scrollbar {
	display: none
}
::-webkit-scrollbar {
	width: 5px;
	background-color: #1c1c1c;
}
::-webkit-scrollbar-thumb {
	border-radius: 5px;
	-webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
	background-color: #0084cc;
}
::placeholder {
	color: white !important;
}

a,
h1,
h2,
h3,
h4 {
	color: #0084cc !important;
}
a:hover,
p,
span {
	color: #fff !important;
}
* {
	box-shadow: none !important;
}
input[type="text"],
input[type="password"],
input[type="email"],
input[type="search"],
input[type="submit"],
textarea,
select,
.pl-promo,
#pl-promo {
	background: #1c1c1c !important;
	color: #fff !important;
	border: 1px solid #0084cc !important;
}
input[type="text"]::placeholder,
input[type="password"]::placeholder,
input[type="email"]::placeholder,
input[type="search"]::placeholder,
textarea::placeholder {
	color: #fff !important;
}
input[type="submit"]:hover {
	background: #0084cc !important;
	color: #fff !important;
}
/*HEADER*/
.desktop-header,
.title-container,
.title-container > .title {
	background: #1c1c1c !important;
}
.desktop-header .search .fa {
	color: #0084cc;
}
.desktop-header nav .secondary a {
	color: #1c1c1c !important
}
/*HEADER*/
/*MAINPAGE*/
.below,
.document,
.end,
.medium-list.hd .list-item,
.stack .list-item {
	background: #1c1c1c;
}
/*MAINPAGE*/
/*ARTICLE*/
.article-header .metadata {
	color: #fff;
}
.article .body table thead {
	background: #0084cc;
	color: #1c1c1c;
}
.article .body figure figcaption {
	background: #1c1c1c;
	color: #fff;
}
.button.outline,
.button-group .button.outline:first-of-type,
.button-group .button.outline.on {
	border-color: #0084cc;
	color: #0084cc;
}
.button.outline:hover,
.button-group .button.outline.on:hover {
	background: #0084cc;
	color: #1c1c1c !important;
}
.article .body strong {
	color: #0084cc;
}
.article .body figure.video {
	background: #1c1c1c;
}
.article .body figure.video figcaption {
	background: #1c1c1c;
	padding: 0;
}
/*COMMENTS*/
.alert {
	background: #1c1c1c;
	border: 1px solid #0084cc;
}
/*COMMENTS*/
/*ARTICLE*/
/*SUBPAGES*/
/*TUTORIALS*/
.game-grid-block .grid-item {
	background: #1c1c1c;
}
/*TUTORIALS*/
/*REVIEWS*/
.article .body aside > div {
	background: #1c1c1c;
	width: calc(100% - 0px);
}
.article .body aside.no-image::before {
	border-left-color: #0084cc;
}
.article .body aside.no-image::after {
	border-bottom-color: #0084cc;
}
/*REVIEWS*/
/*SEARCH*/
.gsc-control-cse,
.gsc-input-box {
	background: #1c1c1c;
}
input.gsc-input,
.gsc-webResult.gsc-result {
	border: 0 !important;
}
.gs-no-results-result .gs-snippet {
	background: #1c1c1c;
	color: #fc3;
}
.gsc-control-cse .gs-snippet,
.gsc-result-info,
.gsc-results .gsc-cursor-box .gsc-cursor-page {
	color: #fff;
}
.gcsc-find-more-on-google-magnifier {
	fill: #fff;
}
/*SEARCH*/
/*LOGIN*/
.sign-in-overlay .sign-in-content > div,
.form .validator {
	background: #1c1c1c;
	border: 1px solid #0084cc;
}
.form label {
	color: #0084cc;
}
.form input {
	background: #1c1c1c;
	border: 1px solid #0084cc;
	color: #fff;
}
.form input[type="checkbox"] + label {
	color: #fff;
}
/*LOGIN*/
/*COOKIE*/
.cookie-gdpr {
	background: #1c1c1c;
}
.cookie-gdpr .container-1260,
.optanon-alert-box-wrapper {
	border: 1px solid #0084cc;
	background: #1c1c1c !important;
}
/*COOKIE*/
/*NEWSLETTER*/
.newsletter-promo {
	border: 1px solid #0084cc;
	background: #1c1c1c;
}
/*NEWSLETTER*/
/*SUBPAGES*/
/*FOOTER*/
footer .container-1260:after {
	content: "Dark theme by Maciejozaman";
	color: #0084cc;
	cursor: none;
	position: relative;
	left: 307px;
	top: -15px;
}
footer .container-1260:before {
	content: "Fixes and changes by Blyad";
	position: relative;
	top: 355px;
	color: #0084cc75;
}
/*FOOTER*/
/*Fixes and changes by Blyad*/
.touch-header .menu nav a {
	color: #fff !important;
}
.medium-list.mobile .list-item .details,
.medium-list.mobile .list-item .metadata,
.medium-list.portable .list-item .details,
.medium-list.portable .list-item .metadata {
	background: #1c1c1c;
	box-shadow: 15px 15px 15px #00000085 !important;
}
.medium-list.mobile .list-item,
.medium-list.portable .list-item {
	background: #1e1e1e;
}
.stack .list-item .details,
.medium-list.hd .list-item .details,
.touch-header .account {
	background: #1c1c1c;
	box-shadow: 5px 5px 15px #00000085 !important;
}
.mobile-stack .list-item.large .details,
.touch-header,
nav.secondary {
	background: #1c1c1c;
}
.loaded.image,
.popular > .section-title {
	background: #000000cf !important;
}
.desktop-header nav .primary > div,
.article-header .metadata,
.article,
.billboard-container,
.comment-cta,
.game-grid-block,
.author-bio img + .details,
.homepage-billboard-container,
.mobile-mpu-container,
.stack:not(:last-child),
.threaded-comments .filters,
.footer.hd .about,
.footer.hd .eurogamer,
.footer.hd .explore,
.footer.hd .helpful-links,
.footer.hd .network,
.small-list .list-item,
.popular-and-latest,
.game-spotlight:not(:last-child),
.main > .game-spotlight,
.video-player.hd .playlist-item a,
.video-player.loaded,
.threaded-comments .comment[data-parent="0"],
.threaded-comments .comment:not([data-parent="0"]) .details,
.profile-header,
.generic-tabs,
hr,
.game-grid-block .grid-item,
.page-header-slim,
.card.reddit .reddit-post,
.actions-bar,
.mobile-stack,
.mobile-stack .list-item,
.footer.mobile .switcher,
.footer.portable .switcher,
.footer .about,
.footer .eurogamer,
.footer .explore,
.footer .helpful-links,
.footer .network,
.touch-header,
.touch-header,
.touch-header .account,
.medium-list.mobile,
.medium-list.portable,
.compact-archive-item,
.author-header,
.author-header .details {
	border-color: #222;
}
.threaded-comments .comment .body table td blockquote,
.card.reddit .reddit-post:hover {
	border-color: #333;
}
.video-player.hd .playlist-item:hover {
	background: #005e8a24;
}
.stack .list-item .cover,
.video-container,
.article .body figure img {
	background-color: #222;
}
.threaded-comments .comment .body table td blockquote {
	color: #bbb;
}
.button-group .button:not(:last-of-type) {
	border-right: 1px solid #222;
}
.switcher .button-group .button:not(:first-of-type):not(.on) {
	border-left: 1px solid #222;
}
.game-grid-block .grid-item:only-of-type ul li {
	border-right: 1px solid #252525;
}
.section-title:after {
	color: #0084cc;
}
.newsletter-promo,
.notifier,
.roadblock,
.touch-header .menu nav.secondary a {
	background: #00aaff14;
	backdrop-filter: blur(3px);
	border-color: #00aaf329;
}
}
