css:
@-moz-document domain("ravelry.com") {
body, ol, ul, td, th, input, select, table {
    font-family: Verdana, "Lucida Sans Unicode", "Lucida Grande", Arial, Helvetica, sans-serif;
    font-size: 12px;
}
body.supports_subnavigation.with_subnavigation .breadcrumbs--subnavigation {
    font-size: 14px;
}
.c-navigation_indicator__snake {
    border-bottom: 5px solid #bbb;
}
.topics #topic_toolbar {
    background-color: #DAF7F3;
    color: #000;
    border-top: 1px solid #000;
}
#topic_toolbar .title_overlay a {
    color: #000 !important;
    font-weight: normal;
}
.topics #topic_toolbar a, .topics #topic_toolbar a:visited {
    color: #477A85
}
/* soften pagination */
 .pagination a, .page_links .pagination a, .page_buttons a {
    border: 1px solid #000;
    border-radius: 4px;
    background-color: #DAF7F3;
}
a.page_bar__page {
    box-shadow: 3px 3px 2px 0 #ccc;
}
.page_bar__current {
    border: 1px solid #000;
    border-radius: 4px;
    background-color: #72DFD0;
}
.page_bar__hopper {
    border: 1px solid #000;
    border-radius: 4px;
}
/* forum posts look and feel */
 .forum_post_listing .forum_post_row {
    border: 1px solid #ccc;
}
.forum_post_listing .forum_post_header {
    border: 1px solid #ccc;
}
.topics .forum_posts .who .posts, .search_discussions .posts {
    margin-top: 6px;
}
.forum_post__reply_tools {
    min-height: 0px;
}
.forum_post_body {
    min-height: 100px !important;
}
.forum_post_body .forum_post_linked_photo {
    display: block !important;
    margin-top: 12px;
    margin-bottom: 12px;
}
.forum_post_row--badged .forum_post_body {
    min-height: 100px !important;
}
body.topics_show .parent_post {
    margin-left: 5px;
    padding: 10px;
    margin-bottom: 10px;
    color: black;
    border: 1px solid #ccc;
    background-color: #EFFAF9;
}
/* tags */
 ul.tag_set li:not(.tag_set__control), span.tag_word {
    border-width: 1px;
    border-radius: 4px;
    border-color: #72DFD0;
    background-color: #DAF7F3;
}
body.search .filter_box {
    border-width: 1px;
    border-radius: 4px;
    border-color: #72DFD0;
    background-color: #DAF7F3;
}
/* buttons and boxes */
 .clicker_v2 {
    border: 1px solid #000;
    border-radius: 4px;
}
.clicker_v2, .clicker {
    box-shadow: 3px 3px 2px 0 #ccc;
}
.box, .download_plate, .zone_specific_yarn_links .yarn_link_box {
    box-shadow: 3px 3px 2px 0 #ccc;
}
.notebook_card {
    border: 1px solid #ccc;
}
.notebook_card__sidebar {
    border-left: 1px solid #ccc;
}
.box_title, .box_title--standalone {
    padding-right: 0.5em;
}
/* working on stash */
 .notebook_thumbnails {
    justify-content: space-around;
}
/* lighten up orange search buttons */
 #advanced_search_link, #advanced_search_link:visited {
    background-color: #f7beba !important;
    color: #000 !important
}
#advanced_search_link:hover {
    background-color: #f4a9a4 !important
}
#advanced_search_link:active {
    background-color: #f4a9a4 !important
}
.clicker_v2--advanced_search {
    background-image: none;
    background-color: #f7beba !important
}
.clicker_v2--advanced_search:hover {
    background-color: #f4a9a4 !important
}
body.search #remember_bin {
    box-shadow: 3px 3px 2px 0 #ddd;
    border: 1px solid #72DFD0;
}
/* get rid of hard black image borders */
 .photo_gallery__border {
    border: 1px solid #ccc;
    border-radius: 5px;
}
#slideshow img {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.avatar img, .avatar_medium img {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.photo_frame {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.grid_photo__frame {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.media_square_150 .photo {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.enabler_image img {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.queued_project__image .photo_border {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.yarns_index .new_yarns img {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.yarns .chart .thumbnail img {
    border: 1px solid #ccc;
    border-radius: 3px;
}
.yarns .search_result .preview img, .patterns .search_result .preview img {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.patterns_landing__history .tiny_thumbnail {
    border: 1px solid #ccc;
    border-radius: 3px;
}
.patterns .chart a.thumbnail img {
    border: 1px solid #ccc;
    border-radius: 3px;
}
.patterns .featured_pattern .thumbnail {
    border: 1px solid #ccc;
    border-radius: 5px;
}
.avatar_small img {
    border: 1px solid #ccc;
    border-radius: 3px;
}
.marketplace_preview .enabler_image img {
    border: 1px solid #ccc;
    border-radius: 5px;
}
}
