css:
@-moz-document domain("terraria.gamepedia.com") {
/* ::: fixed header ::: */

/* hide very top gamepedia bar */
div#netbar
{
  display: none !important;
}

/* tweak tabs and search box */
body div#mw-head
{
  position: fixed !important;
  top: 0 !important;
  background: url(https://terraria.gamepedia.com/media/6/6b/Terraria_forest_background.jpg) no-repeat fixed center bottom / cover #B6D1B2 !important;
  z-index: 9999 !important;
  padding-bottom: 2px !important;
  border-radius: 0 !important;
}

/* move content up a bit since gamepedia bar is gone */
div#content
{
  margin-top: -9px !important;
}

/* keep searchbox suggestions above everything */
.suggestions
{
  z-index: 99999 !important;
}

/* ::: tweaks ::: */

/* hide footer and other useless stuff */
#footer-and-prefooter,
#footer,
#siteNotice,
#p-lang,
#left-navigation #p-sharing
{
  display: none !important;
}

/* expand most sidebar panels */
#p-tb *,
#p-Portals *
{
  display: block !important;
}
}
