css:
@-moz-document domain("ravelry.com") {
.navigation_v2__logo {
    background-color: #c1ebb0;
    background-image: url(https://images4-f.ravelrycache.com/uploads/EWM/711531862/RavelryYarnLogo.png);
    background-repeat: no-repeat;
    height: 50px;
    margin-left: 20px;
    width: 78px;
}
.navigation_v2__logo:hover {
    background-color: #e7f7e1;
}
.navigation_v2__logo__text {
    height: 0px !important;
}

body.with_touch .navigation_v2__logo {
    background-color: #c1ebb0;
    background-image: url(https://images4-g.ravelrycache.com/uploads/EWM/711570226/RavelryYarnLogoMobile.png);
    background-repeat: no-repeat;
    height: 50px;
    margin-left: 6px;
    width: 116px;}

body.with_touch.with_extra_squinty_device .navigation_v2__logo__secondary, body.with_touch.with_squinty_device .navigation_v2__logo__secondary {
    display: inline-block !important;
    width: 0px;
    height: 0px;
    margin-top: 9px;}
}
