css:
@-moz-document domain("trello.com") {
@font-face {
  font-family: 'Roboto';
  src: url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Light/Roboto-Light-webfont.eot');
  src: local('Roboto Light'), local('Roboto'), url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Light/Roboto-Light-webfont.ttf') format('truetype');
  font-weight: 300;
  font-style: normal;
}
@font-face {
  font-family: 'Roboto';
  src: url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Regular/Roboto-Regular-webfont.eot');
  src: local('Roboto Regular'), local('Roboto'), url('https://dl.dropboxusercontent.com/u/21799076/Roboto%20Font/Regular/Roboto-Regular-webfont.ttf') format('truetype');
  font-weight: 400;
  font-style: normal;
}
@font-face {
  font-family: 'Vazir';
  src: url('https://cdn.jsdelivr.net/npm/vazir-font@26.0.2/dist/Vazir.eot');
  src: local('Vazir'), local('Vazir'), url('https://cdn.jsdelivr.net/npm/vazir-font@26.0.2/dist/Vazir.ttf') format('truetype');
  font-style: normal;
}
@font-face {
  font-family: 'Vazir-Bold';
  src: url('https://cdn.jsdelivr.net/npm/vazir-font@26.0.2/dist/Vazir-Bold.eot');
  src: local('Vazir-Bold'), local('Vazir-Bold'), url('https://cdn.jsdelivr.net/npm/vazir-font@26.0.2/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
  font-style: normal;
}
@font-face {
  font-family: 'Vazir-Black';
  src: url('https://cdn.jsdelivr.net/npm/vazir-font@26.0.2/dist/Vazir-Black.eot');
  src: local('Vazir-Black'), local('Vazir-Black'), url('https://cdn.jsdelivr.net/npm/vazir-font@26.0.2/dist/Vazir-Black.ttf') format('truetype');
  font-weight: 700;
  font-style: normal;
}
@font-face {
  font-family: 'Vazir-Code-Hack';
  src: url('https://cdn.jsdelivr.net/npm/vazir-code-font@1.1.2/dist/Vazir-Code-Hack.eot');
  src: local('Vazir-Code-Hack'), local('Vazir-Code-Hack'), url('https://cdn.jsdelivr.net/npm/vazir-code-font@1.1.2/dist/Vazir-Code-Hack.ttf') format('truetype');
  font-weight: normal;
  font-style: normal;
}

/* ========================================= */
body {
    font-family: Vazir, Roboto,tahoma, "Helvetica Neue", Arial, Helvetica, sans-serif !important;
    font-size: 14px;
    line-height: 1.7em;

}
body.body-board-view {
    background-color: rgba(0, 150, 210, 1.0)!important;
}
textarea {
    font-family: Vazir, Roboto!important;
    direction: rtl;
}

strong {
 	font-family: Vazir-Bold, Roboto!important;
    direction: rtl;
    font-weight: bold;

}
.description-content h1,h2,textarea.mod-card-back-title {
 	font-family: Vazir-Black, Roboto!important;
    direction: rtl;
    font-weight: 700;
    letter-spacing: -0.5px;
}
.description-content h3 {
    font-family: Vazir-Bold, Roboto!important;
    direction: rtl;
    font-weight: Bold;
    letter-spacing: 0px;
}
.description-content p {
    text-align: justify;
    text-justify: inter-character;
}
.description-content blockquote {
    border-left: 0px;
    border-right: 1px solid rgba(9,30,66,.13);
    margin: 12px 11px 12px 0px;
    padding: 0 12px 0 0;
}
pre, code {
    font-family: Vazir-Code-Hack, Monospace!important;
    direction: ltr;
    font-weight: normal;
}
.phenom {
	direction: rtl;
    margin-right: 40px!important;
    margin-left: 0!important;
    text-align: justify;
}

.phenom-creator, .phenom-icon {
    right: -40px!important;
    left: 0!important;
}
.board-menu {
    margin-top: 0!important;
    position: fixed!important;
	z-index: 60!important;    
}
.list {
}
.list-card-title {
    font-size: 13px;
    line-height: 1.7em;
}
.list-card-operation {
    right: auto;
    left: 3px;
    padding: 2px;
}
.checklist-item-details-text {
    direction: rtl;
    text-align: justify;
}
.card-label.mod-card-front {
    float: right;
}
.board-wrapper.is-show-menu .board-header-btns.mod-right {
    visibility: visible;
    right: -303px;
}
.header-logo-default:after {
    color: white;
    top: 21px;
    position: absolute;
}
.checklist-item {
    padding-left: 0px!important;
    padding-right: 40px!important;
}
.checklist-item-checkbox {
    right: 5px !important;
    left: inherit;
}
.checklist-item-details .checklist-item-row .checklist-item-text-and-controls {
    flex-direction: row-reverse!important;
}

.card-label.mod-card-front {
    text-align: center;
    line-height: initial;
    font-size: 8px;
    height: 12px;
}
.comment-container p {
    direction: rtl;
    text-align: justify;
}
}
