@-moz-document domain("twitter.com") {
/* accessible outlined retweet button */
/* Ver.20210812 by nukumori.org is licensed under CC BY-NC-SA */
/* https://userstyles.org/styles/185536/ */
/* Created: June 20, 2020 */
/* Updated: August 12, 2021 */
/* *** give us your feedback and donate us *** */
/* special thanks to https://stackoverflow.com/a/18330345 */
:root {
  --twitter-default-icon-green: rgb(23, 191, 99);
  --custom-unretweet-outline: var(--custom-green-normal, var(--twitter-default-icon-green));
  --custom-unretweet-fill: #000;
}
body[style*="background-color: #FFFFFF;"],
body[style*="background-color: rgb(255, 255, 255);"] {
  --custom-unretweet-fill: #fff;
}
[role="dialog"] [style*="background-color: rgba("][style*="0.9);"] [data-testid="unretweet"] {
  --custom-unretweet-fill: #000;
}
[role="dialog"] [style*="background-color: rgba("][style*="0.9);"] [data-testid="unretweet"] {
  --custom-unretweet-outline: #fff;
}

[data-testid="unretweet"] svg {
  filter: drop-shadow(1px 1px 0px var(--custom-unretweet-outline)) drop-shadow(-1px 1px 0px var(--custom-unretweet-outline)) drop-shadow(1px -1px 0px var(--custom-unretweet-outline)) drop-shadow(-1px -1px 0px var(--custom-unretweet-outline));
}
[data-testid="unretweet"] path {
  fill: var(--custom-unretweet-fill);
}
}
