css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.underfoule.net") {

	body { background: #2C2C2C !important; color: #eee !important; }

	* { font: 10pt "Arial"; }

	a { color: #eee !important; }

	hr { border:1px solid #222; }

        table,td, .threads{border:none !important;  background: #2C2C2C !important;}
        .reflinkpreview {opacity:0.9;background:#111 !important;border:none !important;}
        .head {background: #1C1C1C !important;border:none !important;}
        .head h1{color:#777 !important;border:none !important;}

        .threadldiv, .thread, .midhead, .newthread, .post {background:#1C1C1C !important;padding:1px;border:none !important;}
        .threadldiv a, .thread a, .midhead a, .newthread a {background:#2C2C2C !important;padding:1px;}

        #menu {display:none !important;}
        #postform {padding-bottom:10px;}
        #thread {border-bottom:1px solid #FFF;}
        #main { width:99% !important; margin: 0 0 0 -180px; background: #2C2C2C !important;}

	#navtop, #navbot { background: #000; color: #000 !important; width: 100%; text-align: center; height: 12pt; font-size: 8pt !important; }

	#navtop *, #navbot * { color: #eee; font-size: 8pt; display: inline; }

	#navtop a:hover, #navbot a:hover { background: #555; }

	form { margin: 1em; }
        .filesize {margin:0 0 0 -50px; padding-bottom:0px !important;display:block;}

        #ad {display:none;}

	.inputtext, textarea { width:400px; color: #222;}

	.rules { display: none; }
        .replymode {background:#222 !important;}
	td.postblock { font-size:11px; background: #222 !important; border: 1px solid #999 !important; color: #aaa !important; }
       
	td.postblock * { font-size: 12px; }

 	td.reply, td.replyhl { background-color:#222 !important; border: 1px solid #111 !important; color: #eee !important;-moz-border-radius: 8px;  min-width:500px !important;}

	td.replyhl { background-color:#4B4B4B !important; border: 1px solid #4B4B4B !important;	color: #eee !important; }

	.postername, .filesize, span.filetitle, .filesize a, a.linkmail, a.quotejs, .replytitle, .commentpostername, .omittedposts, .abbr { color: #999!important; font-size: 9pt;}

	span.filetitle, .replytitle { color: #e15014 !important; }

	#hd, small, font[size="1"], font[size="2"], #ft, #filter-button, #option-button, #navtopr, #navbotr, .logo,.logo font b span, .doubledash, img.rotating, input[type="button"], td[align="right"] { display: none !important; }

	.pages { background: #444 !important; border: 1px solid #555 !important; }

	.pages td { color: #444 !important; font-size: 4pt; }

	.pages td a, .pages td b { color: #eee !important; font-size: 8pt; }

	.pages td b { background-color: #000; }

	.pages input { display: none !important; }

	.pages td:first-child { display: none;}

	pre, textarea, input:not([type="button"]):not([type="checkbox"]):not([type="submit"]):not([type="reset"]), select { background: #333 !important; font-size: 9pt ! important; color: #FFF !important; -moz-appearance: none; border:1px solid #333 !important; }

	input[type="submit"] { background: #444 !important; color: #aaa !important; border: 1px solid gray; -moz-appearance: none; font-size: 9pt; }

	th { background: #222 !important; }

	th font { font-size: 9pt; }

	input[type="file"] { opacity: 0.5 !important;}

	td.deletebuttons { font-size: 8pt !important; color:#eee !important; }

}
