css:
@-moz-document domain("userstyles.org") {
#view-responsive, .navigation, html {
    background-color: #000;
}
#footer {
    width: 100%;
    background: rgb(46, 62, 73);
    background: linear-gradient(90deg, rgba(46, 62, 73, 1) 69%, rgba(47, 84, 110, 1) 74%, rgba(255, 255, 255, 1) 88%);
    padding-top: 33px;
    padding-bottom: 32px;
    display: flex;
    font-size: 12px;
    z-index: 3;
    position: relative;
    color: #ccc;
}
.CodeMirror {
    background-color: #aaa;
}
#nav-view-responsive, input, textarea {
    background-color: #000;
    color:#ccc !important;
}
.PageContent {
    background-color: #111;
    padding: 10px;
    color: #ccc !important;
}
.style_category_card_wrapper {
    width: 218px;
    height: 81px;
    border-radius: 4px;
    background-color: #222;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.08);
    margin-right: 22px;
    margin-bottom: 20px;
    cursor: pointer;
    color: #ccc;
}
#category_style_card_title, .navigation .links-box .navigation-link .navigation-title {
    font-size: 16px;
    font-weight: 500;
    text-align: center;
    color: #ccc;
}
.navigation .links-box .navigation-link:hover {
    background-color: #222;
    cursor: pointer;
}
.navigation .links-box .navigation-link {
    box-shadow: 0 -1px 0 0 #444, 0 0 0 0 #444;
    padding: 13px 0 12px 0;
    display: flex;
    align-items: center;
}
.us-stylecard--short {
    background-color: #111;
    border-radius: 4px;
    box-shadow: 0 5px 10px 0 rgba(0, 0, 0, 0.08);
    box-sizing: border-box;
    display: inline-block;
    height: 294px !important;
    margin: 10px;
    padding: 0;
    overflow: hidden;
    transition: all 0.2s ease;
    width: 280px;
}
.us-stylecard--short:hover {
    background-color: #222;
}
h4 a:hover {
    color: #fff !important;
    text-decoration: none;
}
h4 a {
    color: #ccc !important;
    text-decoration: none;
}
#copyrights {
    color: #ccc;
}
#notes_textarea {
    background-color: #f5f7fa00;
    border: none;
    resize: vertical;
    overflow-y: scroll;
    font-size: 15px;
    color: #2c3e50;
    max-height: 300px;
    width: 728px;
    max-width: 100%;
    overflow-y: hidden;
}
#overlay-info {
    display: flex;
    flex-direction: row;
    justify-content: space-between;
    align-items: center;
    position: absolute;
    width: 880px;
    height: 91px;
    opacity: 0.95;
    background-color: rgba(0, 0, 0, 0.95);
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
    top: 262px;
}
.us-stylecard--long {
    background-color: #111
}
}
