css:
@-moz-document domain("naekranie.pl") {
::-webkit-scrollbar-track {
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, 0.3);
             border-radius: 0px;
             background-color: #212121;
        }
        html[hide-scrollbar="true"] ::-webkit-scrollbar{display:none}
        ::-webkit-scrollbar {
             width: 5px;
             background-color: #212121;
        }
        ::-webkit-scrollbar-thumb {
             border-radius: 0px;
             -webkit-box-shadow: inset 0 0 0px rgba(0, 0, 0, .3);
             background-color: #5cccf5;
        }

.d-lg-block, .d-lg-block.d-none.video-wrapper, .kc5ad5aab > .content, .kc5ad5aab {
    display: none !important;
    visibility: hidden !important;
}
.article-body-content.content, .justify-content-between.flex-row.br-bottom.br-top.d-flex, .art-header.content, #wo-breadcrumbs, .container-fluid,  .d-flex.floating.social-buttons.flex-row, .content.navigationBottomContainer, .grid-content.content, .show-title, #header-main, .movie-content.content, .pt-15px.px-30px, #driver-section > .slick-dots, .homepage-content.content, .modal-xl.modal-dialog > .modal-content > .modal-body, #login-modal iframe, .iframe-full.iframe-resposive, body, .show-actions-embed .btn.btn-outline-secondary, .nav-menu .nav-item .dropdown-menu {
    background: #000 !important;
    border-color: #222 !important;
}
.br-left.right-content {
    background: #000;
}
.search.my-sm-0.btn, .login.my-sm-0.mx-sm-2.whiteBg.btn, #disqus_ne_login, #disqus_thread > iframe, .btnBoxFooter.btn, .containerBoxFooter > .br-bottom, .h-caps-line:after, .h-caps-line .see-more {
    background: #151515 !important;
    border-color: #222 !important;
}
*, .navbar-light .navbar-nav .nav-link, .dropdown-item, .sidebarTitle, .post-cards .card.card-medium .card-meta h2, .post-cards .card.card-medium .card-meta h2 a, .post-cards .card.card-small .card-meta h2, .post-cards .card.card-small .card-meta h2 a, .post-cards .card .card-meta h2, .post-cards .card .card-meta h2 a, #home-articles .filters li a, #home-news .filters li a, #home-reviews .filters li a {
    color: #eee;
}
h1, h2, h3, h4, h5, h6  {
    color: #eee !important;
}
div.bestText.odd:nth-of-type(1) > span, div.bestText.odd:nth-of-type(3) > span, .show.dropdown-menu, .bestText a.dropdown-item:not(:last-child), .rating-box {
    background: #050505 !important;
    border-color: #222 !important;
}
div.bestText.even:nth-of-type(2) > span, div.bestText.even:nth-of-type(4) > span {
    background: #020202 !important;
}
input, textarea, .searchInput.input-group .input-group-prepend .btn.whiteBg, #movie-cinema-city-selector {
    background: #151515 !important;
    border-color: #222 !important;
    color: #eee !important;
}
.recommendContainer ul.slick-dots li:not(.slick-active) button, .weekPremiereContainer ul.slick-dots li:not(.slick-active) button  {
    background: #332f2f !important;
}
.birthdayContainer .media-body a, .birthdayContainer .media-body h4, .movie-description .rankingText p, .movie-reviewContainer p, form.form-style .form-check-label, form>.form-style .form-check-label, .article-text .quote {
    color: #c6c6c6;
}
.gradient-top {
    background: linear-gradient(to bottom,rgba(106, 106, 106, 0) 0,rgba(255,255,255,.01) 1%,#000 100%);
}
.ribbon, .review-meta {
    background: #0092ff !important;
}
.article-text .multi-rating-box .title h2 .rating, .multi-rating-box .title h2 .rating, .article-text .multi-rating-box h2>span, .article-text .multi-rating-box h3>span, .multi-rating-box h2>span, .multi-rating-box h3>span  {
    color: #cf0 !important;
}
.reviewer-table .reviewer-cell:nth-child(2n+1), .reviewer-table .reviewer-cell:nth-child(2n+2), .reviewer-table, .br-left.right-content, .br-left, .br-right, li.stream-element, .br-top {
    border-color: #080808;
}
ul+.more-stream {
    border-color: #151515;
}
iframe {
    border-color: #080808 !important;
}
.navigationBottomContainer .navList li a, .post-cards .card .card-meta .excerpt, .avatar .add time {
    color: #aaa;
}
.birthdayContainer .media-body p {
    color: #737f97;
}
.containerBoxFooter .btnBoxFooter, .content.navigationBottomContainer {
    outline: 1px solid #080808;
}
.navbar .navbar-brand > img.img-responsive, .slick-slider .slick-next, .slick-slider .slick-prev {
    filter: invert(100%);
} 
.pagination.pag-arrows .page-link, .pagination.pag-arrows .page-link:hover {
    background: #010101;
    color: #ccc;
}
.pagination.pag-arrows .active {
    color: #00aff0;
    background-color: #010101;
}
}
