css:
@-moz-document domain("forum.playcanvas.com") {
.topic-body {
    width: 100%;
}

.timeline-container {
    margin-left: 70%;
}

code, pre {
    font-family: "Fira Code", Consolas, Menlo, Monaco, "Lucida Console", "Liberation Mono", "DejaVu Sans Mono", "Bitstream Vera Sans Mono", "Courier New", monospace;
    font-size: 14px;
}

.post-menu-area {
    margin: 1px 0;
}

.post-actions {
    margin-bottom: 1px;
}

p {
    display: block;
    margin-block-start: 1px;
    margin-block-end: 1px;
    margin-inline-start: 0px;
    margin-inline-end: 0px;
}
}
