css:
@-moz-document url-prefix("https://searchengines.guru/") {
body, h1, h2, h3, h4, .page-header, .font-title, .font-regular, .tabs__item {
    font-family: sans-serif;
}
.top-block {
	display: none;
}

body {
    background-color: #ccc;
    max-width: 1280px;
    margin: 20px auto;
}

/* Шапка */

.page-header {
    border-radius: 6px 6px 0 0;
    border-bottom: solid 2px #828282;
    background: linear-gradient(0deg, #3b3b3b, #828282);
}
.page-header__logo a img {
    max-width: 150px;
    margin: 6px;
}
.page-header__menu-item_selected a {
    background-color: rgba(0, 0, 0, 0.1); 
}
.page-header__menu-item a {
    text-decoration: none;
    font-size: 12px;
    text-transform: uppercase;
    display: flex;
    justify-content: center;
    align-items: center;
    height: 40px;
    padding: 4px 12px 4px;
}

/* Контент */

.main-content {
    background: #dfdfdf;
    padding: 0 30px;
    display: flex;
    flex-direction: row-reverse;
}

/* Основная колонка */
.main-content__column-mid {
    padding-right: 50px;
    width: 75%;
    margin: 0;
    border: 0;
}
.middle-block {
    display: none;
}
.topics-list__item:nth-child(2n+1) {
    background: #eaeaea;    
    box-shadow: 0 2px 3px -2px rgba(0,0,0,0.1);
}
.topics-list__title {
    padding-top: 10px;
    padding-bottom: 10px;
}

/* Боковое меню */

.main-content__column-left {
	width: 25%;    
    padding-left: 40px;
    border: 0;
}

.sidebar-menu__item a {
	font-size: 14px;
    transition: all .2s ease;
    padding-top: 5px;
    padding-bottom: 5px;
    border-radius: 2px;
    margin: 2px 0 0 0;
    line-height: 1;    
}

.sidebar-menu__item a:hover {
    color: #000;
    background: #738fbf30;
}

.sidebar-menu__item_active>a, .sidebar-menu__item_active>span {
    background: #738fbf;
    color: #fff;    
    text-transform: uppercase;
}

.sidebar-menu__item_active>a:hover, .sidebar-menu__item_active>span:hover {
    background: #738fbf;
    color: #fff;    
    text-transform: uppercase;
}

/* Сообщения */

.comment {
    position: relative;
    padding-bottom: 20px;
    margin-bottom: 20px;
    min-height: 110px;
    box-shadow: 0 10px 20px -10px rgba(0,0,0,0.2);
    border-radius: 6px;
    overflow: hidden;
    background: #f0f0f0;
    padding: 10px 30px 10px 0;
}

/* Личный кабинет */
.profile-view__avatar img {
    border-radius: 6px;
}
.my-topics__item:nth-child(2n) {
    background: #eee;
}
.tabs__navigation {
    border-bottom: 1px solid #738fbf;
}
.tabs__item {
    margin-right: 0px;
    padding: 10px;    
}
.tabs__item {
    font-size: 16px;
    font-weight: bold;
}
.tabs__item:after {
    background: #738fbf;
}
.tabs__item_selected {
    background: #738fbf;
    border-radius: 2px 2px 0 0;
}
.tabs__item_selected a {
    color: #fff;
}

.tabs__item_selected .tabs__count {    
    color: #fff;
}
.my-topics__breadcrumbs a {
    color: #8e8e8e;
    letter-spacing: -0.5px;
}
.my-topics__breadcrumbs a:last-child {
    font-weight: 700;
    color: #738fbf;
}
}
