css:
@-moz-document domain("playcanvas.com") {
.font-regular, .pcui-element, .pcui-boolean-input.pcui-multiple-values:after, .pcui-treeview-item.pcui-treeview-item-rename>.pcui-treeview-item-contents>.pcui-text-input>input {
    font-family: Ubuntu, Roboto, 'Proxima Nova Regular', 'Helvetica Neue', Arial, Helvetica, sans-serif;
    font-weight: normal;
    font-style: normal;
    font-size: 14px;
}

.fixedFont, .pcui-label.pcui-multiple-values:before, .pcui-text-input>input, .pcui-text-input.pcui-multiple-values:before, .pcui-text-area-input>textarea, .pcui-entity-input>.pcui-label, .pcui-entity-input.pcui-multiple-values:before, .pcui-select-input-value, .pcui-select-input-list .pcui-label, .pcui-select-input-tag>.pcui-label, .pcui-inspector>.pcui-label-group>.pcui-label:not(:first-child) {
    font-family: "Fira Code", inconsolatamedium, Monaco, Menlo, 'Ubuntu Mono', Consolas, source-code-pro, monospace;
    font-weight: normal;
    font-size: 11px;
}


.pcui-inspector>.pcui-label-group>.pcui-label:first-child {
    font-size: 13px;
    width: 35%;
}


.pcui-panel-header-title {
    font-size: 14px;
}

}
