css:
/* i really want this to be global */
:root {
    --bg-primary-dark: #121212;
    --bg-secondary-dark: #272727;
    --bg-tertiary-dark: #343434;
    --bg-fourth-dark: #414141;
    --primary-color-text: white;
    --secondary-color-text: grey;
    --dioguinho-red-color-text: #c90000;
    --dioguinho-red-color-bg: #c90000
}
@-moz-document url-prefix(https://dioguinho.pt/) {

    .wf-poppins-n6-active body{
         background-color:var(--bg-primary-dark);
         color: var(--primary-color-text);
    }

    .main-nav-dark .main-nav.fixed-nav,
    .theme-header,
    .main-nav-boxed .main-nav:not(.fixed-nav), .main-nav-boxed .fixed-nav .main-menu-wrapper, .theme-header:not(.main-nav-boxed) .fixed-nav .main-menu-wrapper,
    .container.header-container,
    #tie-wrapper{
    	background-color: var(--bg-secondary-dark) !important;
    }

    .post-bottom-meta a,
    .wp-embed,
    .dark-skin, .dark-skin body,
    .top-nav,
    .main-nav-dark.main-nav-boxed .main-nav .main-menu-wrapper {
                                  background-color: var(--bg-tertiary-dark) !important;
    }

    .main-nav-dark .main-menu .menu-sub-content,
    .wf-poppins-n6-active input,
    .wf-poppins-n6-active textarea {
                                  background-color: var(--bg-fourth-dark) !important;
    }

    .wf-poppins-n6-active .entry a{
        color: var(--dioguinho-red-color-text) !important;
    }

    .thumb-overlay .post-meta, .thumb-overlay .thumb-title a,
    .wp-embed,
    .entry-header .post-meta .meta-item,
    .timeline-widget li .date,
    .main-nav-dark .main-nav input, .top-nav-dark .top-nav input,
    .components>li>a,
    .main-nav-dark .main-menu .menu>li a,
    #go-to-top,
    .organic-thumbs-feed-01 .video-description,
    .top-nav a:not(.button):not(:hover),
    .topbar-today-date{
                                            color: var(--primary-color-text) !important;
    }

    .magazine2 .post-footer-on-bottom{
                                            border-color: var(--secondary-color-text) !important;
    }

    .wf-poppins-n6-active p.wp-embed-heading,
    .organic-thumbs-feed-01 .video-title,
    .wf-poppins-n6-active a,
    .entry-header .entry-sub-title{
                                            color: var(--secondary-color-text) !important;
    }

    #tie-body [type='submit'], #tie-body .button, #tie-body .generic-button a, #tie-body .generic-button button, #tie-body .theme-header .header-nav .comp-sub-menu a.button.guest-btn:hover, #tie-body .theme-header .header-nav .comp-sub-menu a.checkout-button, #tie-body nav.main-nav .menu > li.tie-current-menu > a, #tie-body nav.main-nav .menu > li:hover > a, #tie-body .main-menu .mega-links-head:after, #tie-body #main-nav .mega-menu.mega-cat .cats-horizontal li a.is-active, #tie-body #mobile-menu-icon:hover .nav-icon, #tie-body #mobile-menu-icon:hover .nav-icon:before, #tie-body #mobile-menu-icon:hover .nav-icon:after, #tie-body .search-in-main-nav.autocomplete-suggestions a.button, #tie-body .search-in-top-nav.autocomplete-suggestions a.button, #tie-body .spinner > div, #tie-body .post-cat, #tie-body .pages-numbers li.current span, #tie-body .multiple-post-pages > span, #tie-body #tie-wrapper .mejs-container .mejs-controls, #tie-body .mag-box-filter-links a:hover, #tie-body .slider-arrow-nav a:not(.pagination-disabled):hover, #tie-body .comment-list .reply a:hover, #tie-body .commentlist .reply a:hover, #tie-body #reading-position-indicator, #tie-body #story-index-icon, #tie-body .videos-block .playlist-title, #tie-body .review-percentage .review-item span span, #tie-body .tie-slick-dots li.slick-active button, #tie-body .tie-slick-dots li button:hover, #tie-body .digital-rating-static, #tie-body .timeline-widget li a:hover .date:before, #tie-body #wp-calendar #today, #tie-body .posts-list-counter li:before, #tie-body .cat-counter a + span, #tie-body .tie-slider-nav li span:hover, #tie-body .fullwidth-area .widget_tag_cloud .tagcloud a:hover, #tie-body .magazine2:not(.block-head-4) .dark-widgetized-area ul.tabs a:hover, #tie-body .magazine2:not(.block-head-4) .dark-widgetized-area ul.tabs .active a, #tie-body .magazine1 .dark-widgetized-area ul.tabs a:hover, #tie-body .magazine1 .dark-widgetized-area ul.tabs .active a, #tie-body .block-head-4.magazine2 .dark-widgetized-area .tabs.tabs .active a, #tie-body .block-head-4.magazine2 .dark-widgetized-area .tabs > .active a:before, #tie-body .block-head-4.magazine2 .dark-widgetized-area .tabs > .active a:after, #tie-body .demo_store, #tie-body .demo #logo:after, #tie-body .widget.tie-weather-widget, #tie-body span.video-close-btn:hover, #tie-body #go-to-top, #tie-body .latest-tweets-widget .slider-links .button:not(:hover) {

                                            color: var(--primary-color-text) !important;
                                            background-color: var(--dioguinho-red-color-bg) !important;
    }
}
