.inputGroup.inputGroup--joined .input+.inputGroup-text, .inputGroup.inputGroup--joined .input+.input, .inputGroup.inputGroup--joined .inputGroup-text+.input,.pageNav-page:not(:first-child){border-left:1px solid #31435d;}

