css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("userscripts.org") {

#divMenu {
top: 74px !important;
/*============================================= Updater button position*/
   right: 430px !important;
/*=============================================*/

-moz-border-radius: 4px !important;}

div#divUpdater > a.USO_outdatedLink,
a[style="position: absolute; right: 3px; top: 3px; font-size: x-small;"] {
top: 71px !important;
/*============================================= close button position*/
   right: 385px !important;
/*=============================================*/

-moz-border-radius-bottomleft: 0px !important;
-moz-border-radius-bottomright: 4px !important;
-moz-border-radius-topleft: 0px !important;
-moz-border-radius-topright: 4px !important;}

#divMenu:hover, div#divUpdater > a.USO_outdatedLink:hover,
a[style="position: absolute; right: 3px; top: 3px; font-size: x-small;"]:hover {
background: #FF7700 !important;}

#divUpdater, #divOptions {
background-color:rgba(30, 30, 30, .95) !important;
padding: 10px 0 10px 10px !important;
color: #FFF !important;}

#divRegisteredScripts {
background-color:rgba(86, 86, 86, 1.0) !important;}

#divOptions a, #divMenu a {color: #FFF !important;}

#divMenu, div#divUpdater > a.USO_outdatedLink,
a[style="position: absolute; right: 3px; top: 3px; font-size: x-small;"] {
background: #FF9900 !important;
border: 1px solid #FF7700 !important;
padding: 1px 8px !important;
color: #FFF !important;
font-size: 12px !important;
text-shadow: 0 -1px 1px #888888 !important;}
}
