css:
@-moz-document domain("watchserieshd.tv") {
body {
    background-color: #151515;
}

body .wrapper .content a:hover, .wrapper .breadcrumb a:hover {
    color: #1787ad;
}

body .wrapper .content a:hover, .wrapper .breadcrumb a:hover {
    color: #fff;
}

.header {
    background-color: rgb(0, 0, 0);
}

.wrapper .video_c_player ul.video_module .vid_info>span a {
    color: #fff;
}

h1, h2, h3, h4, h5, h6 {
    padding: 0;
    margin: 0;
    font-size: 14px;
    color: #1787ad;
}

body .wrapper .page-title h1 {
    font-size: 28px;
    line-height: 22px;
    color: #1787ad;
    margin-bottom: 0;
    padding-top: 11px;
}

.wrapper p {
    color: #ffffff;
}

.wrapper .breadcrumb {
    color: #fff;
}

.wrapper a {
    color: #ffffff;
}

.wrapper .wpb_button.wpb_btn-primary {
    background-color: #841414;
    border-radius: 5px;
}

.wrapper .wpb_button.wpb_btn-primary:hover {
    background-color: #5b0e0e !important;
    border-radius: 5px;
}

.wrapper .wpb_button.wpb_regularsize {
    border-radius: 5px;
}

.wrapper .wpb_button.wpb_regularsize:hover {
    border-radius: 10px;
}

.icon-twitter-1, .icon-flickr, .icon-facebook, .icon-user-2, .icon-gplus, .icon-pinterest, .icon-vkontakte, .icon-youtube, .header-social-icons .icon-user, .micon-logout {
    border-left: 1px solid #1787ad;
}

.logo {
    border-right: 1px solid #1787ad;
}

.red-color-scheme.followed_title, .header-social-icons .user-icon {
    background-color: #db4a3f00 !important;
}

.wpb_button.wpb_btn-info.wpb_btn-small:hover {
    background-color: #276579;
    border-radius: 10px;
}

.wpb_btn-info:hover {
    background-color: #0f5973;
}

#list .wpb_button.wpb_btn-small, .category .wpb_button.wpb_btn-small {
    padding: 8px 10px;
    border-radius: 3px;
}

.wrapper .wpb_button.wpb_btn-primary:hover {
    background-color: #1787ad;
    border-radius: 10px;
}

.tags {
    color: white;
}

.tags li a {
    background-color: #525252;
}

.tags li a:hover {
    background-color: #1787ad;
}

.video-page-desc-wrapper:before {
    background: linear-gradient(to bottom,rgba(250,250,250,0) 0%,rgb(34, 34, 34) 100%);
}

.side_menu_seperator {
    background-color: #1787ad;
}

.video_share_text {
    color: #fff;
}

.video-page-info .page_title {
    color: #fff;
}

.sidebar_menu {
    background: #222222;
    border: 1px solid #1787ad;
    border-radius: 6px;
    border-right-style: none;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
}

.sidebar_menu ul.primary-menu>li.menu-item:hover {
    background: #444444;
}

.sidebar_menu ul.primary-menu {
    color:#1787ad;
}

.sidebar_menu ul.primary-menu li.menu-item.before-css:hover {
    color:#fff;
}

.sidebar_menu ul.primary-menu li.menu-item.before-css:hover:before {
    color:#fff;
}

.sidebar_menu ul.primary-menu li a {
    color: #1787ad;
}

.sidebar_menu ul.primary-menu>li.menu-item>a:hover {
    color:#fff;
}

.sidebar_menu ul.primary-menu li.menu-item>a::after {
     background: #1787ad;
}

.sidebar_menu ul.primary-menu>li.menu-item:hover>a {
    color:#fff;
}

.sidebar_menu ul.primary-menu li.menu-item.micon-anime a {
    color: #00eaff;
}

.sidebar_menu ul.primary-menu li.menu-item.micon-anime a {
    filter: invert(100%) grayscale(100%);
    color: #f00;
}

.sidebar_menu ul.primary-menu li.menu-item.micon-anime a:hover{
    color: #f00;
    filter: grayscale(20%);
}

.video-container-sidebar .sidebar_border {
    background: #ff000000;
}

.video-container-sidebar {
    background: #3c3c3c;
    border-radius: 5px;
}

.video-page-desc {
    color: #ffffff;
    padding-bottom: 50px;
}

.anime_muti_link ul {
    padding: 0px 0 !important;
}

.anime_muti_link ul li {
    background-color: #222;
    border-bottom: solid 1px #1787ad;
}

.anime_muti_link ul li a {
    color: #1787ad;
}

.anime_muti_link ul li a.active {
    background-color: #444;
}

.anime_muti_link ul li .doamin {
    color: #fff;
}

.anime_muti_link ul li .link {
    color: #fff;
}

.anime_muti_link ul li a:hover {
    background-color: #444;
}

.anime_muti_link ul li:last-child, .anime_muti_link ul li:first-child {
    border-bottom: solid 1px #1787ad;
}

.anime_muti_link ul li a span {
    background-color: #415794;
}

.form-contact label {
    color: #ffffff;
}

.footer_seperator {
    background-color: #1787ad;
}

.footer-container.modern .bottom-menu-container .footer-menu li a {
    color: #fff;
}

.footer-container.modern .bottom-menu-container .footer-menu li:first-child {
    background: #222;
    border-radius: 5px;
}

.footer-container.modern .bottom-menu-container .footer-menu li {
    margin: 5px;
    background: #222;
    border-radius: 5px;
}

.footer-container.modern .bottom-menu-container .footer-menu li:first-child:hover {
background:#444444;
}

.footer-container.modern .bottom-menu-container .footer-menu li:hover {
background:#444444;
}

.footer_menu_sep {
    background-color: #1787ad;
}

.footer-wrapper .footer-container .bottom-menu-container .footer-menu li:hover a {
    color: #1787ad;
}

.footer-container .scroll-to-top:hover:before {
    color: #1787ad;
}
}
