css:
@-moz-document domain("ravelry.com") {
.navigation_v2__logo {
    background-image: url(https://style-cdn.ravelrycache.com/images/assets/icons/navigation/yarns.svg?v=12);
    background-repeat: no-repeat;
    height: 50px;
    margin-left: 20px;
    width: 78px;
    background-position: left center;
    background-size: 30px 30px;
}

body.with_touch .navigation_v2__logo {
    background-image: url(https://style-cdn.ravelrycache.com/images/assets/icons/navigation/yarns.svg?v=12);
    background-repeat: no-repeat;
    height: 50px;
    margin-left: 6px;
    width: 116px;}

.navigation_v2__logo__text {
    height: 32px;
    vertical-align: top;
    padding-top: 9px;
    padding-left: 2px;
    width: 104px;
}
}
