@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("manager-magazin.de") {

/* Hintergrund blau */
body, #mmWrapper {background: #253960 url("data:image/png;base64,") top left repeat-x !important;
	color: #fff !important;
}

/* new defaults for all links */
a:link, .link { color: #93AaD9 !important; }
a:visited, .link:visited { color: #455980 !important; }

H2, H1, H3, H4, .mmFirst.mmRessortTeaserLeft, .mmFirst.mmRessortTeaserRight, .mmRessortTeaserLeft, .mmRessortTeaserRight { color: #A5B9E0 !important; }
SPAN { color: #7589B0 !important; }

#mmMainContent, .mmbchartbox , .mmNavigation.mmClearfix , .mmcodepunt.mmmusterdep, .mmClearfix { background: #253960 !important; }
.mmInfoBox, .fmhbox, .zinsTicker_row_even, .zinsTicker_row_odd  { background: #253960 !important; }

#mmMultiPagerHeadlines > UL > LI:first-child { color: #808085 !important; }

#mmBreadcrumb, #mmTopicHeader, .mmCredit, #mmArticleTopAsset > DIV > DIV > P { background: #253960 !important; }

#mmArticleTopAsset > DIV > DIV { padding-bottom: 0px !important; background: none !important;}

}
