@-moz-document domain("mpgh.net") {
/* Cba make extension atm so if you want to make changes: Stylish recommends that all CSS modifications made be appended with !important.
* READ MORE HERE: https://github.com/stylish-userstyles/stylish/wiki/Overwriting-page-styles */

/* made by Psychotic */


/* OVERALL/PARENT DIV */
 html {
    background:#161616 !important;
    background-image:url("https://i.imgur.com/N14oMEl.png") !important;
    background-repeat:repeat-x;
    /* https://i.imgur.com/jDDgdlJ.png */
}
    .darkmode-background {
    background:#25252525 !important;
}
    .darkmode-toggle {
    visibility:hidden;
}
    .darkmode-layer--button {
    visibility:hidden;
}
.body_wrapper {
    border:1px solid #303030 !important;
}
body, legend {
    font-family:'Roboto', sans-serif !important;
    background:#252525 !important;
}
body > div > button.darkmode-toggle {
    visibility:hidden;
}
body > div > div.outercontainer {
    /* inner-container/outer-container, this is the color of, mainly, everything below the sub forum links */
    background:#202020 !important;
}
body > div > div.outercontainer > div {
    /* this is .inner-container. Fixing the border on the right, it's an image. */
    background:none !important;
}
/* HEADER: INCLUDES LOGO, QUICK LINKS, ADVERTISEMENTS, USER AREA, FORUM LINKS */
 .doc_header {
    /* color, borders of area surrounding logo */
    border-left:1px solid #2c2c2c !important;
    border-right:1px solid #2c2c2c !important;
}
div.logo_container {
    /* background and size */
    background:#1a1a1a !important;
}
/* START: NAVIGATION BAR */
 body > div > div.outercontainer > div > div.above_body > div:nth-child(2) {
    /* border of navbar */
    border:none !important;
}
body > div > div.outercontainer > div > div.above_body > div:nth-child(2) > div {
    background:#101010 !important;
}
#header > div.logo_container > div:nth-child(1) > a.logo-image > img {
    /*logo */
    content:url("https://www.mpgh.net/forum/images/emotions/porra.png") !important; 
    margin-left:50px !important;
}

a[href="https://www.youtube.com/watch?v=KFOTXZCFVOw"] {
	display:none !important;
}
#header > div.logo_container > div:nth-child(1):before {
    /*content:"MPGH" !important;
font-size:48px;
color:#064a6e;
z-index:999;
font-weight:700;*/
    background:url('https://i.imgur.com/gSttJqw.png');
}
#navtabs > li:nth-child(1) > a {
    font-family:'Roboto', sans-serif !important;
}
div[id^=post_message] {
    font-family:'Roboto', sans-serif !important;
}
div[id^=post_message] > blockquote > span, div[id^=post_message] > blockquote {
    font-family:'Roboto', sans-serif !important;
    font-size:1em !important;
}
.postbitlegacy .postrow, .eventbit .eventrow, .postbitdeleted .postrow, .postbitignored .postrow {
    font-family:'Roboto', sans-serif !important;
    font:none !important;
    font-size:1em !important;
    color:#EEE !important;
}
/* ALL EDITS PERTAINING POSTBIT */
 .postbitlegacy .postdetails, .eventbit .eventdetails {
    background:#252525 !important;
    border-right:1px solid #0f0f0f !important;
    border-top:1px solid #303030 !important;
    border-left:1px solid #0f0f0f !important;
}
.postbitlegacy .userinfo {
    /* userinfo (left side of post) */
    background-color:#252525 !important;
    border-right:1px solid #161616 !important;
}
.postbitlegacy .postrow.has_after_content {
    border-bottom:#0f0f0f !important;
}
.signature {
    border-top:1px solid #303030 !important;
}
.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
    /* post footer */
    background:#252525 !important;
    border-top:1px solid #303030 !important;
    border-bottom:1px solid #0f0f0f !important;
    padding:10px 0 8px !important;
}
.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
    background:#252525 !important;
    border:1px solid #0f0f0f !important;
}
.postbit, .postbitlegacy, .eventbit {
    border:1px solid #303030 !important;
    padding-right:2px;
}
.postbit .postfoot, .postbitlegacy .postfoot, .eventbit .eventfoot {
